
import { Vagga } from '../types';

export const DHAMMAPADA_DATA: Vagga[] = [
  {
    id: 1,
    title: "YAMAKA VAGGA",
    translation: "Syair Berpasangan",
    verses: [
      { number: 1, pali: "Mano-pubbangama dhamma...", translation: "Pikiran adalah pelopor dari segala sesuatu, pikiran adalah pemimpin, pikiran adalah pembentuk. Bila seseorang berbicara atau berbuat dengan pikiran jahat, maka penderitaan akan mengikutinya, bagaikan roda pedati mengikuti langkah kaki lembu yang menariknya." },
      { number: 2, pali: "Mano-pubbangama dhamma...", translation: "Pikiran adalah pelopor dari segala sesuatu, pikiran adalah pemimpin, pikiran adalah pembentuk. Bila seseorang berbicara atau berbuat dengan pikiran murni, maka kebahagiaan akan mengikutinya, bagaikan bayang-bayang yang tak pernah meninggalkan bendanya." },
      { number: 3, pali: "Akkocchi mam avadhi mam...", translation: "“Ia menghina saya, ia memukul saya, ia mengalahkan saya, ia merampas milik saya.” Selama seseorang masih menyimpan pikiran seperti itu, maka kebencian tak akan pernah berakhir." },
      { number: 4, pali: "Akkocchi mam avadhi mam...", translation: "“Ia menghina saya, ia memukul saya, ia mengalahkan saya, ia merampas milik saya.” Jika seseorang sudah tidak lagi menyimpan pikiran-pikiran seperti itu, maka kebencian akan berakhir." },
      { number: 5, pali: "Na hi verena verani...", translation: "Kebencian tak akan pernah berakhir, apabila dibalas dengan kebencian. Tetapi, kebencian akan berakhir, Bila dibalas dengan tidak membenci. Inilah satu hukum abadi." },
      { number: 6, pali: "Pare ca na vijananti...", translation: "Sebagian besar orang tidak mengetahui bahwa, dalam pertengkaran mereka akan binasa; tetapi mereka, yang dapat menyadari kebenaran ini; akan segera mengakhiri semua pertengkaran." },
      { number: 7, pali: "Subhanupassim viharantam...", translation: "Seseorang yang hidupnya hanya ditujukan pada hal-hal yang menyenangkan, yang inderanya tidak terkendali, yang makannya tidak mengenal batas, malas serta tidak bersemangat, maka Mara (Penggoda) akan menguasai dirinya. bagaikan angin yang menumbangkan pohon yang lapuk." },
      { number: 8, pali: "Asubhanupassim viharantam...", translation: "Seseorang yang hidupnya tidak ditujukan pada hal-hal yang menyenangkan, yang inderanya terkendali, sederhana dalam makanan, penuh keyakinan serta bersemangat, maka Mara (Penggoda) tidak dapat menguasai dirinya. bagaikan angin yang tidak dapat menumbangkan gunung karang." },
      { number: 9, pali: "Anakkasavo kasavam...", translation: "Barang siapa yang belum bebas, dari kekotoran-kekotoran batin. yang tidak memiliki pengendalian diri, serta tidak mengerti kebenaran. sesungguhnya tidak patut, ia mengenakan jubah kuning." },
      { number: 10, pali: "Yo ca vantakasavassa...", translation: "Tetapi, ia yang telah dapat, membuang kekotoran-kekotoran batin, teguh dalam kesusilaan. memiliki pengendalian diri. serta mengerti kebenaran. maka sesungguhnya ia patut, mengenakan jubah kuning." },
      { number: 11, pali: "Asare saramatino...", translation: "Mereka yang menganggap, ketidak-benaran sebagai kebenaran. dan kebenaran sebagai ketidak-benaran. maka mereka yang mempunyai, pikiran keliru seperti itu, tak akan pernah dapat, menyelami kebenaran." },
      { number: 12, pali: "Saram ca sarato natva...", translation: "Mereka yang mengetahui, kebenaran sebagai kebenaran. dan ketidak-benaran sebagai ketidak-benaran, maka mereka yang mempunyai, pikiran benar seperti itu, akan dapat menyelami kebenaran." },
      { number: 13, pali: "Yatha agaram ducchannam...", translation: "Bagaikan hujan, yang dapat menembus rumah beratap tiris. demikian pula nafsu, akan dapat menembus pikiran yang tidak dikembangkan dengan baik." },
      { number: 14, pali: "Yatha agaram succhannam...", translation: "Bagaikan hujan, yang tidak dapat menembus rumah beratap baik. demikian pula nafsu, tidak dapat menembus pikiran yang telah dikembangkan dengan baik." },
      { number: 15, pali: "Idha socati pecca socati...", translation: "Di dunia ini ia bersedih hati. di dunia sana ia bersedih hati. pelaku kejahatan akan bersedih hati, di kedua dunia itu. ia bersedih hati dan meratap, karena melihat perbuatannya sendiri, yang tidak bersih." },
      { number: 16, pali: "Idha modati pecca modati...", translation: "Di dunia ini ia bergembira. Di dunia sana ia bergembira. Pelaku kebajikan, bergembira di kedua dunia itu. Ia bergembira dan bersuka cita karena, melihat perbuatannya sendiri yang bersih." },
      { number: 17, pali: "Idha tappati pecca tappati...", translation: "Di dunia ini ia menderita. Di dunia sana ia menderita. Pelaku kejahatan menderita di kedua dunia itu. Ia meratap ketika berpikir, “Aku telah berbuat jahat,”, dan ia akan lebih menderita lagi, ketika berada di alam sengsara." },
      { number: 18, pali: "Idha nandati pecca nandati...", translation: "Di dunia ini ia bahagia. Di dunia sana ia berbahagia. Pelaku kebajikan, berbahagia di kedua dunia itu. Ia akan berbahagia ketika berpikir, “Aku telah berbuat bajik”, dan ia akan lebih berbahagia lagi, ketika berada di alam bahagia." },
      { number: 19, pali: "Bahum pi ce sahitam bhasamano...", translation: "Biarpun seseorang banyak membaca kitab suci, tetapi tidak berbuat sesuai ajaran, maka orang lengah itu, sama seperti gembala sapi yang menghitung sapi milik orang lain. Ia tak akan memperoleh, manfaat kehidupan suci." },
      { number: 20, pali: "Appam pi ce sahitam bhasamano...", translation: "Biarpun seseorang sedikit membaca kitab suci, tetapi berbuat sesuai dengan ajaran, menyingkirkan nafsu indria, kebencian dan ketidaktahuan, memiliki pengetahuan benar, dan batin yang bebas dari nafsu, tidak melekat pada apapun, baik di sini maupun di sana; maka ia akan memperoleh, manfaat kehidupan suci." }
    ]
  },
  {
    id: 2,
    title: "APPAMADA VAGGA",
    translation: "Kewaspadaan",
    verses: [
      { number: 21, pali: "Appamado amatapadam...", translation: "Kewaspadaan adalah jalan menuju kekekalan; kelengahan adalah jalan menuju kematian. Orang yang waspada tidak akan mati, Tetapi orang yang lengah seperti orang yang sudah mati." },
      { number: 22, pali: "Etam visesato natva...", translation: "Setelah mengerti hal ini dengan jelas, orang bijaksana akan bergembira dalam kewaspadaan dan bergembira dalam praktek para ariya." },
      { number: 23, pali: "Te jhayino satatika...", translation: "Orang bijaksana yang tekun bersamadhi, hidup bersemangat dan selalu bersungguh-sungguh, pada akhirnya mencapai nibbana (kebebasan mutlak)." },
      { number: 24, pali: "Utthanavato satimato...", translation: "Orang yang penuh semangat, selalu sadar, murni dalam perbuatan, Memiliki pengendalian diri, hidup sesuai dengan Dhamma, dan selalu waspada, maka kebahagiaannya akan bertambah." },
      { number: 25, pali: "Utthanen'appamadena...", translation: "Dengan usaha yang tekun, semangat, disiplin, dan pengendalian diri, hendaklah orang bijaksana, membuat pulau bagi dirinya sendiri, yang tidak dapat ditenggelamkan oleh banjir." },
      { number: 26, pali: "Pamadam anuyunjanti...", translation: "Orang dungu yang berpengertian dangkal, terlena dalam kelengahan, sebaliknya, orang bijaksana senantiasa menjaga kewaspadaan. seperti menjaga harta yang paling berharga." },
      { number: 27, pali: "Ma pamadam anuyunjetha...", translation: "Jangan terlena dalam kelengahan, Jangan terikat pada kesenangan-kesenangan indria. Orang yang waspada dan rajin bersamadhi, akan memperoleh kebahagiaan sejati." },
      { number: 28, pali: "Pamadam appamadena...", translation: "Bilamana orang bijaksana, telah mengatasi kelengahan dengan kewaspadaan, maka ia akan bebas dari kesedihan, seakan memanjat menara kebijaksanaan, dan memandang orang-orang yang menderita di sekelilingnya, seperti seseorang yang berdiri diatas gunung memandang mereka yang berada di bawah." },
      { number: 29, pali: "Appamatto pamattesu...", translation: "Waspada di antara yang lengah, berjaga di antara yang tertidur, orang bijaksana akan maju terus, bagaikan seekor kuda yang tangkas berlari meninggalkan kuda yang lemah di belakangnya." },
      { number: 30, pali: "Appamadena maghava...", translation: "Dengan menyempurnakan kewaspadaan, Dewa Sakka dapat mencapai tingkat pemimpin di antara para dewa. Sesungguhnya, kewaspadaan itu akan selalu dipuji, dan kelengahan akan selalu dicela." },
      { number: 31, pali: "Appamadarato bhikkhu...", translation: "Seorang bhikkhu, yang bergembira dalam kewaspadaan, dan melihat bahaya dalam kelengahan, akan maju terus membakar semua rintangan batin, bagaikan api membakar kayu, baik yang besar maupun yang kecil." },
      { number: 32, pali: "Appamadarato bhikkhu...", translation: "Seorang bhikkhu yang bergembira dalam kewaspadaan, dan melihat bahaya dalam kelengahan, tak akan terperosok lagi, Ia sudah berada di ambang pintu nibbana." }
    ]
  },
  {
    id: 3,
    title: "CITTA VAGGA",
    translation: "Pikiran",
    verses: [
      { number: 33, pali: "Phandanam capalam cittam...", translation: "Pikiran itu mudah goyah dan tidak tetap; pikiran susah dikendalikan dan dikuasai. Orang bijaksana meluruskannya bagaikan seorang pembuat panah meluruskan anak panah." },
      { number: 34, pali: "Varigo-va thale khitto...", translation: "Bagaikan ikan yang dikeluarkan dari air dan dilemparkan ke atas tanah, pikiran itu selalu menggelepar. Karena itu cengkeraman dari Mara harus ditaklukkan." },
      { number: 35, pali: "Dunniggahassa lahuno...", translation: "Sukar mengendalikan pikiran yang binal dan senang mengembara sesuka hatinya. Adalah baik untuk mengendalikan pikiran, suatu pengendalian pikiran yang baik akan membawa kebahagiaan." },
      { number: 36, pali: "Sududdasam sunipunam...", translation: "Pikiran sangat sulit untuk dilihat, amat lembut dan halus, pikiran bergerak sesuka hatinya. Orang bijaksana selalu menjaga pikirannya, seseorang yang menjaga pikirannya akan berbahagia." },
      { number: 37, pali: "Durangamam ekacaram...", translation: "Pikiran itu selalu mengembara jauh, tidak berwujud, dan terletak jauh di lubuk hati. Mereka yang dapat mengendalikannya, akan bebas dari jeratan Mara." },
      { number: 38, pali: "Anavatthitacittassa...", translation: "Orang yang pikirannya tidak teguh, yang tidak mengenal ajaran yang benar, yang keyakinannya selalu goyah, orang seperti itu tidak akan sempurna kebijaksanaannya." },
      { number: 39, pali: "Anavassutacittassa...", translation: "Orang yang pikirannya tidak dikuasai oleh nafsu dan kebencian, yang telah mengatasi keadaan baik dan buruk, di dalam diri orang yang selalu sadar seperti itu tidak ada lagi ketakutan." },
      { number: 40, pali: "Kumbhupamam kayam imam...", translation: "Dengan mengetahui bahwa tubuh ini rapuh bagaikan tempayan, hendaknya seseorang memperkokoh pikirannya bagaikan benteng kota, dan melenyapkan Mara dengan senjata kebijaksanaan. Ia harus menjaga apa yang telah dicapainya, dan hidup tanpa ikatan lagi." },
      { number: 41, pali: "Aciram vat'ayam kayo...", translation: "Aduh, tak lama lagi tubuh ini akan terbujur di atas tanah, dibiarkan saja, tanpa kesadaran, bagaikan sebatang kayu yang tidak berguna." },
      { number: 42, pali: "Diso disam yam tam kayira...", translation: "Luka dan kesakitan macam apa pun, dapat dibuat oleh orang yang saling bermusuhan atau saling membenci. Namun pikiran yang diarahkan secara salah, akan melukai seseorang jauh lebih berat." },
      { number: 43, pali: "Na tam mata pita kayira...", translation: "Bukan dengan pertolongan ibu, ayah, ataupun sanak keluarga; namun pikiran yang diarahkan dengan baik, yang akan membantu dan mengangkat derajat seseorang." }
    ]
  },
  {
    id: 4,
    title: "PUPPHA VAGGA",
    translation: "Bunga-Bunga",
    verses: [
      { number: 44, pali: "Ko imam pathavim vijessati...", translation: "Siapakah yang akan menaklukkan dunia ini beserta alam Yama dan alam Dewa? Siapakah yang akan menyelidiki Jalan Kebajikan yang telah diterangkan dengan jelas, seperti seorang perangkai bunga yang pandai memilih bunga?" },
      { number: 45, pali: "Sekho pathavim vijessati...", translation: "Seorang Sekha (siswa yang masih berlatih) akan menaklukkan dunia ini beserta alam Yama dan alam Dewa. Seorang siswa yang masih berlatih ini akan menyelidiki jalan kebajikan yang telah diajarkan dengan jelas, seperti seorang perangkai bunga yang pandai memilih bunga." },
      { number: 46, pali: "Phenupamam kayam imam viditva...", translation: "Setelah mengetahui bahwa tubuh ini bagaikan busa, dan setelah menyadari sifat mayanya, maka hendaknya seseorang mematahkan bunga nafsu keinginan, dan menghilang dari pandangan raja kematian." },
      { number: 47, pali: "Pupphani heva cinantam...", translation: "Orang yang mengumpulkan bunga-bunga kesenangan indria, yang pikirannya kacau, akan diseret oleh kematian. Bagaikan banjir besar menghanyutkan sebuah desa yang tertidur." },
      { number: 48, pali: "Pupphani heva cinantam...", translation: "Orang yang mengumpulkan bunga-bunga kesenangan indria, yang pikirannya kacau dan tak pernah puas, akan berada di bawah kekuasaan Sang Penghancur (kematian)." },
      { number: 49, pali: "Yathapi bhamaro puppham...", translation: "Bagaikan seorang kumbang mengumpulkan madu dari bunga-bunga tanpa merusak warna dan baunya; demikian pula hendaknya orang bijaksana mengembara dari desa ke desa." },
      { number: 50, pali: "Na paresam vilomani...", translation: "Janganlah memperhatikan kesalahan dan hal-hal yang telah atau belum dikerjakan oleh orang lain. Tetapi, perhatikanlah apa yang telah dikerjakan dan apa yang belum dikerjakan oleh diri sendiri." },
      { number: 51, pali: "Yathapi ruciram puppham...", translation: "Bagaikan sekuntum bunga yang indah tetapi tidak berbau harum; demikian pula akan tidak bermanfaat kata-kata mutiara yang diucapkan oleh orang yang tidak melaksanakannya." },
      { number: 52, pali: "Yathapi ruciram puppham...", translation: "Bagaikan sekuntum bunga yang indah serta berbau harum; demikian pula sungguh bermanfaat kata-kata mutiara yang diucapkan oleh orang yang melaksanakannya." },
      { number: 53, pali: "Yathapi puppharasima...", translation: "Seperti dari setumpuk bunga dapat dibuat banyak karangan bunga; demikian pula hendaknya banyak kebajikan dapat dilakukan oleh manusia di dunia ini." },
      { number: 54, pali: "Na pupphagandho pativatam...", translation: "Harumnya bunga, tidak dapat melawan arah angin. Begitu pula harumnya kayu cendana, bunga tagara dan melati. Tetapi harumnya kebajikan, dapat melawan arah angin; harumnya nama orang bajik dapat menyebar ke segenap penjuru." },
      { number: 55, pali: "Candanam tagaram vapi...", translation: "Harumnya kebajikan, adalah jauh melebihi harumnya kayu cendana, bunga tagara, teratai maupun melati." },
      { number: 56, pali: "Appamatto ayam gandho...", translation: "Tidaklah seberapa, harumnya bunga tagara dan kayu cendana; tetapi harumnya mereka, yang memiliki sila (kebajikan), menyebar sampai ke surga." },
      { number: 57, pali: "Tesam sampannasilanam...", translation: "Mara tak dapat menemukan jejak mereka yang memiliki sila, yang hidup tanpa kelengahan, dan yang telah terbebas melalui Pengetahuan Sempurna." },
      { number: 58, pali: "Yatha samkaradhanasmi...", translation: "Seperti dari tumpukan sampah yang dibuang di tepi jalan, tumbuh bunga teratai yang berbau harum dan menyenangkan hati." },
      { number: 59, pali: "Evam samkarabhutesu...", translation: "Begitu juga di antara orang duniawi, siswa Sang Buddha Yang Maha Sempurna, bersinar menerangi dunia yang gelap ini dengan kebijaksanaannya." }
    ]
  },
  {
    id: 5,
    title: "BALA VAGGA",
    translation: "Orang Bodoh",
    verses: [
      { number: 60, pali: "Digha jagarato ratti...", translation: "Malam terasa panjang bagi orang yang berjaga, satu yojana terasa jauh bagi orang yang lelah; sungguh panjang siklus kehidupan bagi orang bodoh yang tidak mengenal Ajaran Benar." },
      { number: 61, pali: "Caram ce nadigaccheyya...", translation: "Apabila dalam pengembaraan seseorang tak menemukan sahabat yang lebih baik atau sebanding dengan dirinya, maka hendaklah ia tetap melanjutkan pengembaraannya seorang diri. Janganlah bergaul dengan orang bodoh." },
      { number: 62, pali: "Putta m'atthi dhanam m'atthi...", translation: "“Anak-anak ini milikku, kekayaan ini milikku,” demikianlah pikiran orang bodoh. Apabila dirinya sendiri sebenarnya bukan merupakan miliknya, bagaimana mungkin anak dan kekayaan itu menjadi miliknya?" },
      { number: 63, pali: "Yo balo manyati balyam...", translation: "Bila orang bodoh dapat menyadari kebodohannya, maka ia dapat dikatakan bijaksana; tetapi orang bodoh yang menganggap dirinya bijaksana, sesungguhnya dialah yang disebut orang bodoh." },
      { number: 64, pali: "Yavajivam pi ce balo...", translation: "Orang bodoh, walaupun selama hidupnya bergaul dengan orang bijaksana, tetap tidak akan mengerti Dhamma, bagaikan sendok yang tidak dapat merasakan rasa sayur." },
      { number: 65, pali: "Muhuttam api ce vinnu...", translation: "Walaupun hanya sesaat saja orang pandai bergaul dengan orang bijaksana, namun dengan segera ia akan dapat mengerti Dhamma, bagaikan lidah yang dapat merasakan rasa sayur." },
      { number: 66, pali: "Caranti bala dummedha...", translation: "Orang bodoh yang dangkal pengetahuannya, memperlakukan diri sendiri seperti musuh; ia melakukan perbuatan jahat yang akan menghasilkan buah yang pahit." },
      { number: 67, pali: "Na tam kammam katam sadhu...", translation: "Bilamana suatu perbuatan setelah selesai dilakukan membuat seseorang menyesal, maka perbuatan itu tidak baik. Orang itu akan menerima akibat perbuatannya dengan ratap tangis dan wajah yang berlinang air mata." },
      { number: 68, pali: "Tan ca kammam katam sadhu...", translation: "Bila suatu perbuatan setelah selesai dilakukan tidak membuat seseorang menyesal, maka perbuatan itu adalah baik. Orang itu akan menerima buah perbuatannya dengan hati gembira dan puas." },
      { number: 69, pali: "Madhu'va manyati balo...", translation: "Selama buah dari suatu perbuatan jahat belum masak, maka orang bodoh akan menganggapnya manis seperti madu; tetapi apabila buah perbuatan itu telah masak, maka ia akan merasakan pahitnya penderitaan." },
      { number: 70, pali: "Mase mase kusaggena...", translation: "Biarpun bulan demi bulan orang bodoh memakan makanannya dengan ujung rumput kusa, namun demikian ia tidak berharga seperenambelas bagian dari mereka yang telah mengerti Dhamma dengan baik." },
      { number: 71, pali: "Na hi papam katam kammam...", translation: "Suatu perbuatan jahat yang telah dilakukan, tidak segera menghasilkan buah, seperti air susu yang tidak langsung menjadi dadih; demikianlah perbuatan jahat itu membara mengikuti orang bodoh, seperti api yang ditutupi abu." },
      { number: 72, pali: "Yav'adeva anatthaya...", translation: "Orang bodoh mendapat pengetahuan dan kemashuran yang menuju kepada kehancuran, Pengetahuan dan kemashurannya itu akan menghancurkan semua perbuatan baiknya, dan akan membelah kepalanya sendiri." },
      { number: 73, pali: "Asatam bhavanam iccheyya...", translation: "Seorang bhikkhu yang bodoh, menginginkan ketenaran yang keliru, ingin menonjol di antara para bhikkhu, ingin berkuasa dalam vihara-vihara, dan ingin dihormati oleh semua keluarga." },
      { number: 74, pali: "Mam'eva kata manyantu...", translation: "“Biarlah umat awam dan para bhikkhu berpikir bahwa hal ini hanya dilakukan olehku, dalam semua pekerjaan besar atau kecil mereka menunjuk diriku,” demikianlah ambisi bhikkhu yang bodoh itu, dan keinginan serta kesombongannya pun terus bertambah." },
      { number: 75, pali: "Anna hi labhupanisa...", translation: "Ada jalan lain menuju pada keuntungan duniawi, dan ada jalan lain yang menuju ke Nibbana. Setelah menyadari hal ini dengan jelas, hendaklah seseorang bhikkhu siswa Sang Buddha tidak bergembira dalam hal-hal duniawi, tetapi mengembangkan pembebasan diri." }
    ]
  },
  {
    id: 6,
    title: "PANDITA VAGGA",
    translation: "Orang Bijaksana",
    verses: [
      { number: 76, pali: "Nidhinam-va pavattaram...", translation: "Seandainya seseorang bertemu orang bijaksana yang mau menunjukkan dan memberitahukan kesalahan-kesalahannya, seperti orang menunjukan harta karun, hendaklah ia bergaul dengan orang bijaksana itu. Sungguh baik dan tidak tercela bergaul dengan orang yang bijaksana." },
      { number: 77, pali: "Ovadeyya anusamseyya...", translation: "Biarlah ia memberi nasehat, petunjuk, dan melarang apa yang tidak baik, orang bijaksana akan dicintai oleh orang yang baik dan dijauhi oleh orang yang jahat." },
      { number: 78, pali: "Na bhaje papake mitte...", translation: "Jangan bergaul dengan orang jahat, jangan bergaul dengan orang yang berbudi rendah, tetapi bergaullah dengan sahabat yang baik, bergaullah dengan orang yang berbudi luhur." },
      { number: 79, pali: "Dhammapiti sukham seti...", translation: "Ia yang mengenal Dhamma akan hidup berbahagia dengan pikiran yang tenang. Orang bijaksana selalu bergembira dalam ajaran yang dibabarkan oleh para Ariya." },
      { number: 80, pali: "Udakam hi nayanti nettika...", translation: "Pembuat saluran air mengalirkan air, tukang panah meluruskan anak panah, tukang kayu melengkungkan kayu, orang bijaksana mengendalikan dirinya." },
      { number: 81, pali: "Selo yatha ekaghano...", translation: "Bagaikan batu karang yang tak tergoncangkan oleh badai, demikian pula para bijaksana tidak akan terpengaruh oleh celaan maupun pujian." },
      { number: 82, pali: "Yathapi rahado gambhiro...", translation: "Bagaikan danau yang dalam, airnya jernih dan tenang. Demikian pula batin para orang bijaksana, menjadi tentram karena mendengarkan Dhamma." },
      { number: 83, pali: "Sabbatha ve sappurisa...", translation: "Orang bajik membuang kemelekatan terhadap sesuatu, orang suci tidak membicarakan hal-hal yang berkaitan dengan nafsu keinginan. Dalam menghadapi kebahagiaan atau kemalangan, Orang bijaksana tidak menjadi gembira maupun kecewa." },
      { number: 84, pali: "Na attahetu na parassa hetu...", translation: "Seseorang yang arif tidak berbuat jahat demi kepentingannya sendiri ataupun orang lain, demikian pula ia tidak menginginkan anak, kekayaan, pangkat atau keberhasilan dengan cara yang tidak benar. Orang seperti itulah yang sebenarnya luhur, bijaksana, dan berbudi." },
      { number: 85, pali: "Appaka te manussesu...", translation: "Diantara umat manusia hanya sedikit yang dapat mencapai pantai seberang, sebagian besar hanya berjalan hilir mudik di tepi sebelah sini." },
      { number: 86, pali: "Ye ca kho sammadakkhate...", translation: "Mereka yang hidup sesuai dengan Dhamma yang telah diterangkan dengan baik, akan mencapai Pantai Seberang, menyeberangi alam kematian yang sangat sukar diseberangi." },
      { number: 87, pali: "Kanham dhammam vippahaya...", translation: "Meninggalkan rumah dan pergi menempuh kehidupan tanpa rumah, demikian hendaknya orang bijaksana meninggalkan keadaan gelap (kebodohan), dan mengembangkan keadaan terang (kebijaksanaan). Hendaknya ia mencari kebahagiaan pada ketidakmelekatan yang sulit didapat." },
      { number: 88, pali: "Tatrabhirati-miccheyya...", translation: "Dengan meninggalkan semua kesenangan indria dan kemelekatan, demikian hendaknya orang bijaksana membersihkan dirinya dari noda-noda pikiran." },
      { number: 89, pali: "Yesam sambodhiyangesu...", translation: "Mereka yang telah menyempurnakan pikirannya dalam Tujuh Faktor Penerangan, yang tanpa ikatan, yang bergembira dengan batin yang bebas, yang telah bebas dari kekotoran batin, yang bersinar, maka sesungguhnya mereka telah mencapai Nibbana dalam kehidupan sekarang ini juga." }
    ]
  },
  {
    id: 7,
    title: "ARAHANTA VAGGA",
    translation: "Arahat",
    verses: [
      { number: 90, pali: "Gataddhino visokassa...", translation: "Orang yang telah menyelesaikan perjalanannya yang telah terbebas dari segala hal, Yang telah menghancurkan semua ikatan, maka dalam dirinya tidak ada lagi demam nafsu." },
      { number: 91, pali: "Uyyunjanti satimanto...", translation: "Orang yang telah telah sadar dan meninggalkan kehidupan rumah tangga, tidak lagi terikat pada tempat kediaman. Bagaikan kawanan angsa yang meninggalkan kolam demi kolam, demikianlah mereka meninggalkan tempat kediaman demi tempat kediaman." },
      { number: 92, pali: "Yesam sannicayo natthi...", translation: "Mereka yang tidak lagi mengumpulkan harta duniawi, yang sederhana dalam makanan, yang telah mencapai “Kebebasan Mutlak”, maka jejak mereka tidak dapat dilacak, bagaikan burung-burung di angkasa." },
      { number: 93, pali: "Yassasava parikkhina...", translation: "Ia yang telah memusnahkan semua kekotoran batin, yang tidak lagi terikat pada makanan, yang telah menyadari Kebebasan Mutlak, maka jejaknya tidak dapat dilacak, bagaikan burung-burung di angkasa." },
      { number: 94, pali: "Yassindriyani samatham gatani...", translation: "Ia yang telah menaklukkan dirinya, bagaikan seorang kusir mengendalikan kudanya, yang telah bebas dari kesombongan dan kekotoran batin, maka para dewa pun akan mengasihi orang suci seperti ini." },
      { number: 95, pali: "Pathavisamo no virujjhati...", translation: "Bagaikan tanah, demikian pula orang suci. Tidak pernah marah, teguh pikirannya bagaikan tugu kota (indakhila), bersih tingkah lakunya bagaikan kolam tak berlumpur. Bagi orang suci seperti ini tak ada lagi siklus kehidupan." },
      { number: 96, pali: "Santam tassa manam hoti...", translation: "Orang suci yang memiliki pengetahuan sejati, yang telah terbebas, damai dan seimbang batinnya, maka ucapan, perbuatan serta pikirannya senantiasa tenang." },
      { number: 97, pali: "Assaddho akatannu ca...", translation: "Orang yang telah bebas dari ketahyulan, yang telah mengerti keadaan tak tercipta (nibbana), yang telah memutuskan semua ikatan (tumimbal lahir), yang telah mengakhiri kesempatan (baik dan jahat), yang telah menyingkirkan semua nafsu keinginan, maka sesungguhnya ia adalah orang yang paling mulia." },
      { number: 98, pali: "Game va yadi va'ranne...", translation: "Apakah di desa atau di dalam hutan, di tempat yang rendah atau di atas bukit, di mana pun Para Suci menetap, maka tempat itu sungguh menyenangkan." },
      { number: 99, pali: "Ramaniyani arannani...", translation: "Hutan bukan tempat yang menyenangkan bagi orang duniawi, namun di sanalah orang-orang yang telah bebas dari nafsu bergembira, karena mereka tidak lagi mencari kesenangan indria." }
    ]
  },
  {
    id: 8,
    title: "SAHASSA VAGGA",
    translation: "Ribuan",
    verses: [
      { number: 100, pali: "Sahassam api ce vaca...", translation: "Daripada seribu kata yang tak berarti, adalah lebih baik sepatah kata yang bermanfaat, yang dapat memberi kedamaian kepada pendengarnya." },
      { number: 101, pali: "Sahassam api ce gatha...", translation: "Daripada seribu bait syair yang tak berguna, adalah lebih baik sebait syair yang berguna, yang dapat memberi kedamaian kepada pendengarnya." },
      { number: 102, pali: "Yo ca gathasatam bhase...", translation: "Daripada seribu bait syair yang tak bermanfaat, adalah lebih baik satu kata Dhamma, yang dapat memberi kedamaian kepada pendengarnya." },
      { number: 103, pali: "Yo sahassem sahassena...", translation: "Walaupun seseorang dapat menaklukkan ribuan musuh dalam ribuan kali pertempuran, namun sesungguhnya penakluk terbesar adalah orang yang dapat menaklukkan dirinya sendiri." },
      { number: 104, pali: "Atta have jitam seyyo...", translation: "Menaklukkan diri sendiri sesungguhnya lebih baik daripada menaklukkan makhluk lain; orang yang telah menaklukkan dirinya sendiri selalu dapat mengendalikan diri." },
      { number: 105, pali: "Na-eva devo na gandhabbo...", translation: "Tidak ada Dewa, Mara, Gandhabba, ataupun Brahmana, yang dapat mengubah kemenangan dari orang yang telah dapat menaklukkan dirinya sendiri." },
      { number: 106, pali: "Mase mase sahassena...", translation: "Biarpun bulan demi bulan seseorang mempersembahkan seribu korban selama seratus tahun, namun lebih baik jika menghormati orang yang memiliki pengendalian diri, walaupun hanya sesaat saja." },
      { number: 107, pali: "Yo ca vassasatam jantu...", translation: "Biarpun selama seratus tahun seseorang menyalakan api pemujaan di hutan, namun sesungguhnya lebih baik jika ia, walaupun hanya sesaat saja, menghormati orang yang telah memiliki pengendalian diri." },
      { number: 108, pali: "Yam kinci yittham va hutam va...", translation: "Dalam dunia ini, pengorbanan dan persembahan apapun yang dilakukan oleh seseorang selama seratus tahun, untuk memperoleh pahala dari perbuatannya itu, semuanya tidak berharga seperempat bagian pun, daripada penghormatan yang diberikan kepada orang yang hidupnya lurus." },
      { number: 109, pali: "Abhivadanasilissa...", translation: "Ia yang selalu menghormati dan menghargai orang yang lebih tua, kelak akan memperoleh empat hal, yaitu: umur panjang, kecantikan, kebahagiaan, dan kekuatan." },
      { number: 110, pali: "Yo ca vassasatam jive...", translation: "Walaupun seseorang hidup seratus tahun, tetapi memiliki kelakuan buruk dan tak terkendali, sesungguhnya lebih baik adalah kehidupan sehari dari orang yang memiliki sila dan tekun bersamadhi." },
      { number: 111, pali: "Yo ca vassasatam jive...", translation: "Walaupun seseorang hidup seratus tahun, tetapi tidak bijaksana dan tidak terkendali, sesungguhnya lebih baik adalah kehidupan sehari dari orang yang bijaksana dan tekun bersamadhi." },
      { number: 112, pali: "Yo ca vassasatam jive...", translation: "Walaupun seseorang hidup seratus tahun, tetapi malas dan tidak bersemangat, maka sesungguhnya lebih baik kehidupan sehari dari orang berjuang dengan penuh semangat." },
      { number: 113, pali: "Yo ca vassasatam jive...", translation: "Walaupun seseorang hidup seratus tahun, tetapi tidak dapat melihat timbul tenggelamnya segala sesuatu yang berkondisi, sesungguhnya lebih baik kehidupan sehari dari orang yang dapat melihat timbul tenggelamnya segala sesuatu yang berkondisi." },
      { number: 114, pali: "Yo ca vassasatam jive...", translation: "Walaupun seseorang hidup seratus tahun, tetapi tidak dapat melihat “keadaan tanpa kematian” (nibbana), sesungguhnya lebih baik kehidupan sehari dari orang yang dapat melihat “keadaan tanpa kematian”." },
      { number: 115, pali: "Yo ca vassasatam jive...", translation: "Walaupun seseorang hidup seratus tahun, tetapi tidak dapat melihat keluhuran Dhamma (Dhammamuttamam), sesungguhnya lebih baik kehidupan sehari dari orang yang dapat melihat keluhuran Dhamma." }
    ]
  },
  {
    id: 9,
    title: "PAPA VAGGA",
    translation: "Kejahatan",
    verses: [
      { number: 116, pali: "Abhittharetha kalyane...", translation: "Bergegaslah berbuat kebajikan, dan kendalikan pikiranmu dari kejahatan; barang siapa lamban berbuat bajik, maka pikirannya akan senang dalam kejahatan." },
      { number: 117, pali: "Papam ce puriso kayira...", translation: "Apabila seseorang berbuat jahat, hendaklah ia tidak mengulangi perbuatannya itu, dan jangan merasa senang dengan perbuatan itu, sungguh menyakitkan akibat dari memupuk perbuatan jahat." },
      { number: 118, pali: "Punnam ce puriso kayira...", translation: "Apabila seseorang berbuat bajik, hendaklah dia mengulangi perbuatannya itu dan bersuka cita dengan perbuatannya itu, sungguh membahagiakan akibat dari memupuk perbuatan bajik." },
      { number: 119, pali: "Papo pi passati bhadram...", translation: "Pembuat kejahatan hanya melihat hal yang baik, selama buah perbuatan jahatnya belum masak, tetapi bilamana hasil perbuatannya itu telah masak, ia akan melihat akibat-akibatnya yang buruk." },
      { number: 120, pali: "Bhadro pi passati papam...", translation: "Pembuat kebajikan hanya melihat hal yang buruk, selama buah perbuatan bajiknya belum masak, tetapi bilamana hasil perbuatannya itu telah masak, ia akan melihat akibat-akibatnya yang baik." },
      { number: 121, pali: "Mavaman netha papassa...", translation: "Jangan meremehkan kejahatan walaupun kecil, dengan berkata: “Perbuatan jahat tidak akan membawa akibat”. Bagaikan sebuah tempayan akan terisi penuh oleh air yang jatuh setetes demi setetes, demikian pula orang bodoh sedikit demi sedikit memenuhi dirinya dengan kejahatan." },
      { number: 122, pali: "Mavaman netha punnassa...", translation: "Janganlah meremehkan kebajikan walaupun kecil dengan berkata: “Perbuatan bajik tidak akan membawa akibat.” Bagaikan sebuah tempayan akan terisi penuh oleh air yang dijatuhkan setetes demi setetes, demikian pula orang bijaksana sedikit demi sedikit memenuhi dirinya dengan kebajikan." },
      { number: 123, pali: "Vanijo-va bhayam maggam...", translation: "Bagaikan seorang saudagar yang dengan sedikit pengawal membawa banyak harta, menghindari jalan yang berbahaya, demikian pula orang yang mencintai hidup, hendaknya menghindari racun dan hal-hal yang jahat." },
      { number: 124, pali: "Panimhi ce vano nassa...", translation: "Apabila seseorang tidak mempunyai luka di tangan, maka ia dapat menggenggam racun. Racun tidak akan mencelakakan orang yang tidak luka. Tiada penderitaan bagi orang yang tidak berbuat jahat." },
      { number: 125, pali: "Yo appadutthassa narassa...", translation: "Barangsiapa berbuat jahat terhadap orang baik, orang suci, dan orang yang tidak bersalah, maka kejahatan akan berbalik menimpa orang bodoh itu, bagaikan debu yang dilempar melawan angin." },
      { number: 126, pali: "Gabbham eke uppajjanti...", translation: "Sebagian orang terlahir melalui kandungan; pelaku kejahatan terlahir di alam neraka; orang yang berkelakuan baik pergi ke surga; dan orang yang bebas dari kekotoran batin mencapai nibbana." },
      { number: 127, pali: "Na antarikke na samuddamajjhe...", translation: "Tidak di langit, di tengah lautan, di celah-celah gunung atau di manapun juga, dapat ditemukan suatu tempat bagi seseorang untuk dapat menyembunyikan diri dari akibat perbuatan jahatnya." },
      { number: 128, pali: "Na antarikke na samuddamajjhe...", translation: "Tidak di langit, di tengah lautan, di celah-celah gunung atau di manapun juga, dapat ditemukan suatu tempat bagi seseorang untuk dapat menyembunyikan diri dari kematian." }
    ]
  },
  {
    id: 10,
    title: "DANDA VAGGA",
    translation: "Hukuman",
    verses: [
      { number: 129, pali: "Sabbe tasanti dandassa...", translation: "Semua orang takut akan hukuman; semua orang takut akan kematian. Setelah membandingkan orang lain dengan diri sendiri, hendaknya seseorang tidak membunuh atau mengakibatkan pembunuhan." },
      { number: 130, pali: "Sabbe tasanti dandassa...", translation: "Semua orang takut akan hukuman; semua orang mencintai kehidupan. Setelah membandingkan orang lain dengan diri sendiri, hendaknya seseorang tidak membunuh atau mengakibatkan pembunuhan." },
      { number: 131, pali: "Sukhakamani bhutani...", translation: "Barang siapa mencari kebahagiaan untuk dirinya sendiri dengan jalan menganiaya makhluk lain yang juga mendambakan kebahagiaan, maka setelah mati ia tak akan memperoleh kebahagiaan." },
      { number: 132, pali: "Sukhakamani bhutani...", translation: "Barang siapa mencari kebahagiaan untuk dirinya sendiri dengan jalan tidak menganiaya makhluk lain yang juga mendambakan kebahagiaan, maka setelah mati ia akan memperoleh kebahagiaan." },
      { number: 133, pali: "Ma'voca pharusam kanci...", translation: "Jangan berbicara kasar kepada siapapun, karena mereka yang mendapat perlakuan demikian, akan membalas dengan cara yang sama. Sungguh menyakitkan ucapan kasar itu, yang pada gilirannya akan melukaimu." },
      { number: 134, pali: "Sace neresi attanam...", translation: "Apabila engkau berdiam diri bagaikan sebuah gong pecah, berarti engkau telah mencapai nibbana, sebab keinginan membalas dendam tak terdapat lagi dalam dirimu." },
      { number: 135, pali: "Yatha dandena gopalo...", translation: "Bagaikan seorang penggembala menghalau sapinya dengan tongkat ke padang rumput, begitu juga umur tua dan kematian menghalau kehidupan setiap makhluk." },
      { number: 136, pali: "Atha papani kammani...", translation: "Apabila orang bodoh melakukan kejahatan, ia tak mengerti akan akibat perbuatannya. Orang bodoh akan tersiksa oleh perbuatannya sendiri, seperti orang yang terbakar oleh api." },
      { number: 137, pali: "Yo dandena adandesu...", translation: "Seseorang yang menghukum mereka yang tidak patut dihukum dan tidak bersalah, akan segera memperoleh salah satu di antara sepuluh keadaan berikut:" },
      { number: 138, pali: "Vedanam pharusam janim...", translation: "Ia akan mengalami penderitaan hebat, kecelakaan, luka berat, sakit berat, atau bahkan hilang ingatan." },
      { number: 139, pali: "Rajato va upassaggam...", translation: "Atau ditindak oleh raja, atau mendapat tuduhan yang berat, atau kehilangan sanak saudara, atau harta kekayaannya habis." },
      { number: 140, pali: "Ghani va assa agara...", translation: "Atau rumahnya musnah terbakar, dan setelah tubuhnya hancur, orang bodoh ini akan terlahir kembali di alam neraka." },
      { number: 141, pali: "Na naggacariya na jata...", translation: "Bukan dengan cara telanjang, rambut dijalin, badan kotor berlumpur, berpuasa, berbaring di tanah, melumuri tubuh dengan debu, ataupun berjongkok di atas tumit, seseorang yang belum bebas dari keragu-raguan dapat mensucikan diri." },
      { number: 142, pali: "Alankato ce pi samam careyya...", translation: "Walau digoda dengan cara bagaimanapun, tetapi bila seseorang dapat menjaga ketenangan pikirannya, damai, mantap, terkendali, suci murni dan tidak lagi menyakiti makhluk lain, sesungguhnya ia adalah seorang brahmana, seorang samana, seorang bhikkhu." },
      { number: 143, pali: "Hirinisedho puriso...", translation: "Dalam dunia ini jarang ditemukan seseorang yang dapat mengendalikan diri dengan memiliki rasa malu untuk berbuat jahat, yang senantiasa waspada, bagaikan seekor kuda yang terlatih baik dapat menghindari cemeti." },
      { number: 144, pali: "Asso yatha bhadro kasabhivitto...", translation: "Bagaikan seekor kuda yang terlatih baik, walaupun sekali saja merasakan cambukan, segera menjadi bersemangat dan berlari cepat, demikian pula halnya dengan orang yang rajin, penuh keyakinan, memiliki sila, semangat, konsentrasi dan menyelidiki Ajaran Benar, dengan bekal pengetahuan dan tingkah laku sempurna serta memiliki kesadaran, akan segera meninggalkan penderitaan yang berat ini." },
      { number: 145, pali: "Udakam hi nayanti nettika...", translation: "Pembuat saluran air mengatur jalannya air, tukang panah meluruskan anak panah, tukang kayu melengkungkan kayu, orang bajik mengendalikan dirinya sendiri." }
    ]
  },
  {
    id: 11,
    title: "JARA VAGGA",
    translation: "Usia Tua",
    verses: [
      { number: 146, pali: "Ko nu haso kimanando...", translation: "Mengapa tertawa, mengapa bergembira kalau dunia ini selalu terbakar? Dalam kegelapan, tidakkan engkau ingin mencari terang?" },
      { number: 147, pali: "Passa cittakatam bimbam...", translation: "Pandanglah tubuh yang indah ini, penuh luka, terdiri dari rangkaian tulang, berpenyakit serta memerlukan banyak perawatan. Ia tidak kekal serta tidak tetap keadaannya." },
      { number: 148, pali: "Parijinnum idam rupam...", translation: "Tubuh ini benar-benar rapuh, sarang penyakit dan mudah membusuk. Tumpukan yang menjijikkan ini akan hancur berkeping-keping. Sesungguhnya, kehidupan ini akan berakhir dengan kematian." },
      { number: 149, pali: "Yanimani apatthani...", translation: "Bagaikan labu yang dibuang pada musim rontok, demikian pula halnya dengan tulang-tulang yang memutih ini. Kesenangan apakah yang didapat dari memandangnya?" },
      { number: 150, pali: "Atthinam nagaram katam...", translation: "Kota (tubuh) ini terbuat dari tulang belulang yang dibungkus oleh daging dan darah. Di sinilah terdapat kelapukan dan kematian, kesombongan dan iri hati." },
      { number: 151, pali: "Jiranti ve rajaratha sucitta...", translation: "Kereta kerajaan yang indah sekalipun pasti akan lapuk, begitu pula tubuh ini akan menjadi tua. Tetapi ‘Ajaran’ (Dhamma) orang suci tidak akan lapuk. Sesungguhnya dengan cara inilah orang suci mengajarkan kebaikan." },
      { number: 152, pali: "Appassut'ayam puriso...", translation: "Orang yang tidak mau belajar akan menjadi tua seperti sapi; dagingnya bertambah tetapi kebijaksanaannya tidak berkembang." },
      { number: 153, pali: "Aneka jati samsaram...", translation: "Dengan melalui banyak kelahiran aku telah mengembara dalam samsara (siklus kehidupan). Terus mencari, namun tidak kutemukan pembuat rumah ini. Sungguh menyakitkan kelahiran yang berulang-ulang ini." },
      { number: 154, pali: "Gahakaraka dittho'si...", translation: "O, pembuat rumah, engkau telah ku lihat, engkau tak dapat membangun rumah lagi. Seluruh atapmu telah runtuh dan tiangmu belandarmu telah patah. Sekarang batinku telah mencapai ‘Keadaan tak Berkondisi (Nibbana)’. Pencapaian ini merupakan akhir daripada nafsu keinginan." },
      { number: 155, pali: "Acaritva brahmacariyam...", translation: "Mereka yang tidak menjalankan kehidupan suci serta tidak mengumpulkan bekal (kekayaan) selagi masih muda, akan merana seperti bangau tua yang berdiam di kolam yang tidak ada ikannya." },
      { number: 156, pali: "Acaritva brahmacariyam...", translation: "Mereka yang tidak menjalankan kehidupan suci serta tidak mengumpulkan bekal (kekayaan) selagi masih muda, akan terbaring seperti busur panah yang rusak, menyesali masa lampaunya." }
    ]
  },
  {
    id: 12,
    title: "ATTA VAGGA",
    translation: "Diri Sendiri",
    verses: [
      { number: 157, pali: "Attanam ce piyam janna...", translation: "Bila orang mencintai dirinya sendiri, maka ia harus menjaga dirinya dengan baik. Orang bijaksana selalu waspada selama tiga masa dalam kehidupannya." },
      { number: 158, pali: "Attanam eva pathamam...", translation: "Hendaknya orang terlebih dahulu mengembangkan diri sendiri dalam hal-hal yang patut, dan selanjutnya melatih orang lain. Orang bijaksana yang berbuat demikian tak akan dicela." },
      { number: 159, pali: "Attanam ce yatha kayira...", translation: "Sebagaimana ia mengajari orang lain, demikianlah hendaknya ia berbuat. Setelah ia dapat mengendalikan dirinya sendiri dengan baik, hendaklah ia melatih orang lain. Sesungguhnya amat sukar untuk mengendalikan diri sendiri." },
      { number: 160, pali: "Atta hi attano natho...", translation: "Diri sendiri sesungguhnya adalah pelindung bagi diri sendiri. Karena siapa pula yang dapat menjadi pelindung bagi dirinya? Setelah dapat mengendalikan dirinya sendiri dengan baik, ia akan memperoleh perlindungan yang sungguh amat sukar dicari." },
      { number: 161, pali: "Attana'va katam papam...", translation: "Kejahatan yang dilakukan oleh diri sendiri, timbul dari diri sendiri serta disebabkan oleh diri sendiri, akan menghancurkan orang bodoh, bagaikan intan memecah permata yang keras." },
      { number: 162, pali: "Yassa accantadussilyam...", translation: "Orang yang berkelakuan buruk adalah seperti tanaman menjalar maluva yang melilit pohon sala. Ia akan terjerumus sendiri, seperti apa yang diharapkan musuh terhadap dirinya." },
      { number: 163, pali: "Sukarani asadhuni...", translation: "Sungguh mudah untuk melakukan hal-hal yang buruk dan tak bermanfaat, tetapi sungguh sulit untuk melakukan hal-hal yang baik dan bermanfaat bagi diri sendiri." },
      { number: 164, pali: "Yo sasanam arahatam...", translation: "Karena pandangan yang salah orang bodoh menghina ajaran orang mulia, orang suci dan orang bijak. Ia akan menerima akibatnya yang buruk, seperti rumput kastha yang berbuah hanya untuk menghancurkan dirinya sendiri." },
      { number: 165, pali: "Attana'va katam papam...", translation: "Oleh diri sendiri kejahatan dilakukan, oleh diri sendiri seseorang menjadi suci. Suci atau tidak suci tergantung pada diri sendiri. Tak seseorang pun yang dapat mensucikan orang lain." },
      { number: 166, pali: "Attadattham paratthena...", translation: "Jangan karena demi kesejahteraan orang lain lalu seseorang melalaikan kesejahteraan sendiri. Setelah memahami tujuan akhir bagi diri sendiri, hendaklah ia teguh melaksanakan tugas kewajibannya." }
    ]
  },
  {
    id: 13,
    title: "LOKA VAGGA",
    translation: "Dunia",
    verses: [
      { number: 167, pali: "Hinam dhammam na seveyya...", translation: "Janganlah mengejar sesuatu yang rendah, janganlah hidup dalam kelengahan. Janganlah menganut pandangan-pandangan salah, dan janganlah menjadi pendukung keduniawian." },
      { number: 168, pali: "Uttitthe na pamajjeyya...", translation: "Bangun! Jangan lengah! Tempuhlah kehidupan benar. Barang siapa menempuh kehidupan benar, maka ia akan hidup bahagia di dunia ini maupun di dunia selanjutnya." },
      { number: 169, pali: "Dhammam care sucaritam...", translation: "Hendaklah seseorang hidup sesuai dengan Dhamma dan tak menempuh cara-cara jahat. Barang siapa hidup sesuai Dhamma, maka ia akan hidup bahagia di dunia ini maupun di dunia selanjutnya." },
      { number: 170, pali: "Yatha pubbulakam passe...", translation: "Barangsiapa dapat memandang dunia ini seperti melihat busa atau seperti ia melihat fatamorgana, maka Raja Kematian tidak dapat menemukan dirinya." },
      { number: 171, pali: "Etha passath'imam lokam...", translation: "Marilah, pandanglah dunia ini yang seperti kereta kerajaan yang penuh hiasan, yang membuat orang bodoh terlelap di dalamnya. Tetapi bagi orang yang mengetahui, maka tak ada lagi ikatan dalam dirinya." },
      { number: 172, pali: "Yo ca pubbe pamajjitva...", translation: "Barang siapa yang sebelumnya pernah malas, tetapi kemudian tidak malas, maka ia akan menerangi dunia ini bagaikan bulan yang terbebas dari awan." },
      { number: 173, pali: "Yassa papam katam kammam...", translation: "Barang siapa meninggalkan perbuatan jahat yang pernah dilakukan dengan jalan berbuat kebajikan, maka ia akan menerangi dunia ini bagai bulan yang bebas dari awan." },
      { number: 174, pali: "Andhabhuto ayam loko...", translation: "Dunia ini terselubung kegelapan, dan hanya sedikit orang yang dapat melihat dengan jelas. Bagaikan burung-burung kena jerat, hanya sedikit yang dapat melepaskan diri; demikian pula hanya sedikit orang yang dapat pergi ke alam surga." },
      { number: 175, pali: "Hamsa adityapathe yanti...", translation: "Kawanan angsa terbang menuju matahari, orang-orang yang memiliki kekuatan gaib terbang di udara. Orang bijaksana berjalan menuju kesucian setelah menaklukkan Mara beserta bala tentaranya." },
      { number: 176, pali: "Ekam dhammam atitassa...", translation: "Orang yang melanggar salah satu Dhamma (sila keempat, yakni selalu berkata bohong), yang tidak memperdulikan dunia mendatang, maka tak ada kejahatan yang tidak dilakukannya." },
      { number: 177, pali: "Na ve kadariya devalokam yanti...", translation: "Sesungguhnya orang kikir tidak dapat pergi ke alam dewa. Orang bodoh tidak memuji kemurahan hati. Akan tetapi orang bijaksana senang dalam memberi, dan karenanya ia akan bergembira di alam berikutnya." },
      { number: 178, pali: "Pathabya ekarajjenna...", translation: "Ada yang lebih baik daripada kekuasaan mutlak atas bumi, daripada pergi ke surga, atau daripada memerintah seluruh dunia, yakni hasil kemuliaan dari seorang suci yang telah memenangkan arus (sotapatti-phala)." }
    ]
  },
  {
    id: 14,
    title: "BUDDHA VAGGA",
    translation: "Buddha",
    verses: [
      { number: 179, pali: "Yassa jitam navajiyati...", translation: "Beliau yang kemenangannya tak dapat dikalahkan lagi, yang nafsunya telah diatasi dan tidak mengikutinya lagi, Sang Buddha yang tiada bandingnya, yang tanpa jejak nafsu, dengan cara apa akan kaugoda Beliau?" },
      { number: 180, pali: "Yassa jalini visattika...", translation: "Beliau yang tak terjerat dan terlibat nafsu keinginan yang menyebabkan kelahiran, Sang Buddha yang tiada bandingnya, yang tanpa jejak nafsu, dengan cara apa akan kaugoda Beliau?" },
      { number: 181, pali: "Ye jhanapasuta dhira...", translation: "Orang bijaksana yang tekun bersamadhi, yang bergembira dalam kedamaian pelepasan, yang memiliki kesadaran sejati dan telah mencapai Penerangan Sempurna, akan dicintai oleh para dewa." },
      { number: 182, pali: "Kiccho manussapatilabho...", translation: "Sungguh sulit untuk dapat dilahirkan sebagai manusia, sungguh sulit kehidupan manusia, sungguh sulit untuk dapat mendengarkan Ajaran Benar, begitu pula, sungguh sulit munculnya seorang Buddha." },
      { number: 183, pali: "Sabbapapassa akaranam...", translation: "Tidak melakukan segala bentuk kejahatan, senantiasa mengembangkan kebajikan dan membersihkan batin; inilah Ajaran Para Buddha." },
      { number: 184, pali: "Khanti paramam tapo titikkha...", translation: "Kesabaran adalah praktek bertapa yang paling tinggi. “Nibbana adalah tertinggi”‘ begitulah sabda Para Buddha. Dia yang masih menyakiti orang lain sesungguhnya bukanlah seorang pertapa (samana)." },
      { number: 185, pali: "Anupavado anupaghato...", translation: "Tidak menghina, tidak menyakiti, dapat mengendalikan diri sesuai dengan peraturan, memiliki sikap madya dalam hal makan, berdiam di tempat yang sunyi serta giat mengembangkan batin nan luhur; inilah Ajaran Para Buddha." },
      { number: 186, pali: "Na kahapanavassena...", translation: "Bukan dalam hujan emas dapat ditemukan kepuasan nafsu indria. Nafsu indria hanya merupakan kesenangan sekejap yang membuahkan penderitaan." },
      { number: 187, pali: "Api dibbesu kamesu...", translation: "Bagi orang bijaksana yang dapat memahami, hal itu tidak membuatnya bergembira bila mendapat kesenangan surgawi sekalipun. Siswa Sang Buddha Yang Maha Sempurna bergembira dalam penghancuran nafsu-nafsu keinginan." },
      { number: 188, pali: "Bahum ve saranam yanti...", translation: "Karena rasa takut, banyak orang pergi mencari perlindungan ke gunung-gunung, ke arama-arama (hutan buatan), ke pohon-pohon dan ke tempat-tempat pemujaan yang dianggap keramat." },
      { number: 189, pali: "Netam kho saranam khemam...", translation: "Tetapi itu bukanlah perlindungan yang aman, bukanlah perlindungan yang utama. Dengan mencari perlindungan seperti itu, orang tidak akan bebas dari penderitaan." },
      { number: 190, pali: "Yo ca Buddhan ca Dhamman ca...", translation: "Ia yang telah berlindung kepada Buddha, Dhamma dan Sangha, dengan bijaksana dapat melihat Empat Kebenaran Mulia, yaitu:" },
      { number: 191, pali: "Dukkham dukkhasamuppadam...", translation: "Dukkha, sebab dari dukkha, akhir dari dukkha, serta Jalan Mulia Berfaktor Delapan yang menuju pada akhir dukkha." },
      { number: 192, pali: "Etam kho saranam khemam...", translation: "Sesungguhnya itulah perlindungan yang utama. Dengan pergi mencari perlindungan seperti itu, orang akan bebas dari segala penderitaan." },
      { number: 193, pali: "Dullabho purisajanno...", translation: "Sukar untuk berjumpa dengan manusia yang mempunyai kebijaksanaan Agung. Orang seperti itu tidak akan dilahirkan di sebarang tempat. Tetapi dimanapun orang seperti itu dilahirkan, maka keluarganya akan hidup bahagia." },
      { number: 194, pali: "Sukho Buddhanam uppado...", translation: "Kelahiran Para Buddha merupakan sebab kebahagiaan. Pembabaran Ajaran Benar merupakan sebab kebahagiaan. Persatuan Sangha merupakan sebab kebahagiaan. Dan usaha perjuangan mereka yang telah bersatu merupakan sebab kebahagiaan." },
      { number: 195, pali: "Pujarah'e pujayato...", translation: "Ia yang menghormati mereka yang patut dihormati, yakni Para Buddha atau siswa-siswa-Nya, yang telah dapat mengatasi rintangan-rintangan, akan bebas dari kesedihan dan ratap tangis." },
      { number: 196, pali: "Te tadise pujayato...", translation: "Ia yang menghormati orang-orang suci yang telah menemukan kedamaian dan telah bebas dari ketakutan; maka jasa perbuatannya tak dapat diukur dengan ukuran apapun." }
    ]
  },
  {
    id: 15,
    title: "SUKHA VAGGA",
    translation: "Kebahagiaan",
    verses: [
      { number: 197, pali: "Susukham vata jivama...", translation: "Sungguh bahagia jika kita hidup tanpa membenci di antara orang-orang yang membenci; di antara orang-orang yang membenci, kita hidup tanpa benci." },
      { number: 198, pali: "Susukham vata jivama...", translation: "Sungguh bahagia jika kita hidup tanpa penyakit di antara orang-orang yang berpenyakit; di antara orang-orang yang berpenyakit, kita hidup tanpa penyakit." },
      { number: 199, pali: "Susukham vata jivama...", translation: "Sungguh bahagia jika kita hidup tanpa keserakahan di antara orang-orang yang serakah; di antara orang-orang yang serakah, kita hidup tanpa keserakahan." },
      { number: 200, pali: "Susukham vata jivama...", translation: "Sungguh bahagia hidup kita ini apabila sudah tidak terikat lagi oleh rasa ingin memiliki. Kita akan hidup dengan bahagia bagaikan dewa-dewa di alam yang cemerlang." },
      { number: 201, pali: "Jayam veram pasavati...", translation: "Kemenangan menimbulkan kebencian, dan yang kalah hidup dalam penderitaan. Setelah dapat melepaskan diri dari kemenangan dan kekalahan, orang yang penuh damai akan hidup bahagia." },
      { number: 202, pali: "Natthi raga-samo aggi...", translation: "Tiada api yang menyamai nafsu; tiada kejahatan yang menyamai kebencian; tiada penderitaan yang menyamai kelompok kehidupan (khandha); dan tiada kebahagiaan yang lebih tinggi daripada ‘Kedamaian Abadi’ (nibbana)." },
      { number: 203, pali: "Jigaccha-parama roga...", translation: "Kelapuran merupakan penyakit yang paling berat. Segala sesuatu yang berkondisi merupakan penderitaan yang paling besar. Setelah mengetahui hal ini sebagaimana adanya, orang bijaksana memahami bahwa nibbana merupakan kebahagiaan tertinggi." },
      { number: 204, pali: "Arogya parama labha...", translation: "Kesehatan adalah keuntungan yang paling besar. Kepuasan adalah kekayaan yang paling berharga. Kepercayaan adalah saudara yang paling baik. Nibbana adalah kebahagiaan yang tertinggi." },
      { number: 205, pali: "Pavivekarasam pitva...", translation: "Setelah mencicipi rasa penyepian dan ketentraman, maka ia akan bebas dari duka-cita dan tidak ternoda, serta mereguk kebahagiaan dalam Dhamma." },
      { number: 206, pali: "Sahu darsanam ariyanam...", translation: "Bertemu dengan para ariya adalah baik, tinggal bersama mereka merupakan suatu kebahagiaan, orang akan selalu berbahagia bila tak menjumpai orang bodoh." },
      { number: 207, pali: "Balasangatacari hi...", translation: "Seseorang yang sering bergaul dengan orang bodoh pasti akan meratap lama sekali. Karena bergaul dengan orang bodoh adalah penderitaan seperti tinggal bersama musuh. Tetapi, siapa yang tinggal bersama orang bijaksana akan berbahagia, sama seperti sanak keluarga yang kumpul bersama." },
      { number: 208, pali: "Tasma hi dhiran ca pannan ca...", translation: "Karena itu, ikutilah orang yang pandai, bijaksana, terpelajar, tekun, patuh dan mulia; hendaklah engkau selalu dekat dengan orang yang bajik dan pandai seperti itu, bagaikan bulan mengikuti peredaran bintang." }
    ]
  },
  {
    id: 16,
    title: "PIYA VAGGA",
    translation: "Kecintaan",
    verses: [
      { number: 209, pali: "Ayoge yunjam attanam...", translation: "Orang yang memperjuangkan apa yang seharusnya dihindari, dan tidak memperjuangkan apa yang seharusnya diperjuangkan; melepaskan apa yang baik dan melekat pada apa yang tidak menyenangkan, akan merasa iri terhadap mereka yang tekun dalam latihan." },
      { number: 210, pali: "Ma piyehi samaganchi...", translation: "Janganlah melekat pada apa yang dicintai atau yang tidak dicintai. Tidak bertemu dengan mereka yang dicintai dan bertemu dengan mereka yang tidak dicintai, keduanya merupakan penderitaan." },
      { number: 211, pali: "Tasma piyam na kayiratha...", translation: "Oleh sebab itu, janganlah mencintai apapun, karena berpisah dengan apa yang dicintai adalah menyedihkan. Tiada lagi ikatan bagi mereka yang telah bebas dari mencintai dan tidak mencintai." },
      { number: 212, pali: "Piyato jayati soko...", translation: "Dari yang disayangi timbul kesedihan, dari yang disayangi timbul ketakutan; bagi orang yang telah bebas dari yang disayangi, tiada lagi kesedihan maupun ketakutan." },
      { number: 213, pali: "Pemato jayati soko...", translation: "Dari cinta timbul kesedihan, dari cinta timbul ketakutan; bagi orang yang telah bebas dari rasa cinta, tiada lagi kesedihan maupun ketakutan." },
      { number: 214, pali: "Ratiya jayati soko...", translation: "Dari kemelekatan timbul kesedihan, dari kemelekatan timbul ketakutan; bagi orang yang telah bebas dari kemelekatan, tiada lagi kesedihan maupun ketakutan." },
      { number: 215, pali: "Kamato jayati soko...", translation: "Dari nafsu timbul kesedihan, dari nafsu timbul ketakutan; bagi orang yang telah bebas dari nafsu, tiada lagi kesedihan maupun ketakutan." },
      { number: 216, pali: "Tanhaya jayati soko...", translation: "Dari keinginan timbul kesedihan, dari keinginan timbul ketakutan; bagi orang yang telah bebas dari keinginan, tiada lagi kesedihan maupun ketakutan." },
      { number: 217, pali: "Siladassanasampannam...", translation: "Barang siapa sempurna dalam sila dan mempunyai pandangan terang, teguh dalam Dhamma, selalu berbicara benar dan memenuhi segala kewajibannya, maka semua orang akan mencintainya." },
      { number: 218, pali: "Chandajato anakkhate...", translation: "Barang siapa bermaksud ingin mencapai ‘Yang Tak Dinyatakan’ (nibbana), yang batinnya tidak lagi terikat oleh kesenangan indria, orang seperti itu disebut “yang telah pergi ke hilir arus kehidupan”." },
      { number: 219, pali: "Inatipunnam pi ce piyam...", translation: "Setelah lama seseorang pergi jauh dan kemudian pulang ke rumah dengan selamat, maka keluarga, kerabat dan sahabat akan menyambutnya dengan senang hati." },
      { number: 220, pali: "Tatheva katapunnam pi...", translation: "Begitu juga, perbuatan-perbuatan baik yang telah dilakukan akan menyambut pelakunya yang telah pergi dari dunia ini ke dunia selanjutnya, seperti keluarga yang menyambut pulangnya orang tercinta." }
    ]
  },
  {
    id: 17,
    title: "KODHA VAGGA",
    translation: "Kemarahan",
    verses: [
      { number: 221, pali: "Kodham jahe vippajaheyya manam...", translation: "Hendaklah orang menghentikan kemarahan dan kesombongan, hendaklah ia mengatasi semua belenggu. Orang yang tidak lagi terikat pada batin dan jasmani, yang telah bebas dari nafsu-nafsu, tak akan menderita lagi." },
      { number: 222, pali: "Yo ve uppatitam kodham...", translation: "Barangsiapa yang dapat menahan kemarahannya yang telah memuncak seperti menahan kereta yang sedang melaju, ia patut disebut sais sejati. Sedangkan sais lainnya hanya sebagai pemegang kendali belaka." },
      { number: 223, pali: "Akkodhena jine kodham...", translation: "Kalahkan kemarahan dengan cinta kasih dan kalahkan kejahatan dengan kebajikan. Kalahkan kekikiran dengan kemurahan hati, dan kalahkan kebohongan dengan kejujuran." },
      { number: 224, pali: "Saccam bhane na kujjheyya...", translation: "Hendaknya orang berbicara benar, hendaknya orang tidak marah, hendaknya orang memberi walaupun sedikit kepada mereka yang membutuhkan. Dengan tiga cara ini, orang dapat pergi ke hadapan para dewa." },
      { number: 225, pali: "Ahimsaka ye munayo...", translation: "Orang-orang suci yang tidak menganiaya mahluk lain dan selalu terkendali jasmaninya, akan sampai pada ‘Keadaan Tanpa Kematian’ (nibbana); dan setelah sampai pada keadaan itu, kesedihan tak akan ada lagi dalam dirinya." },
      { number: 226, pali: "Sada jagaramananm...", translation: "Mereka yang senantiasa sadar, tekun melatih diri siang dan malam, selalu mengarahkan batin ke nibbana, maka semua kekotoran batin dalam dirinya akan musnah." },
      { number: 227, pali: "Poranam-etam Atula...", translation: "O Atula, hal ini telah ada sejak dahulu dan bukan saja ada sekarang, di mana mereka mencela orang yang duduk diam, mereka mencela orang yang banyak bicara, mereka juga mencela orang yang sedikit bicara. Tak ada seorangpun di dunia ini yang tak dicela." },
      { number: 228, pali: "Na c'ahu na ca bhavissati...", translation: "Tidak pada zaman dahulu, waktu yang akan datang ataupun waktu sekarang, dapat ditemukan seseorang yang selalu dicela maupun yang selalu dipuji." },
      { number: 229, pali: "Yam ce vinnu pasamsanti...", translation: "Setelah memperhatikan secara seksama, orang bijaksana memuji ia yang menempuh kehidupan tanpa cela, pandai serta memiliki kebijaksanaan dan sila." },
      { number: 230, pali: "Nikkham jambonadasseva...", translation: "Siapakah yang layak merendahkan orang tanpa cela seperti sepotong emas murni? Para dewa akan selalu memujinya, begitu pula para brahmana." },
      { number: 231, pali: "Kayappakopam rakkheyya...", translation: "Hendaklah orang selalu menjaga rangsangan jasmani, hendaklah ia selalu mengendalikan jasmaninya. Setelah menghentikan perbuatan-perbuatan jahat melalui jasmani, hendaklah ia giat melakukan perbuatan-perbuatan baik melalui jasmani." },
      { number: 232, pali: "Vacipakopam rakkheyya...", translation: "Hendaklah orang selalu menjaga rangsangan ucapan, hendaklah ia mengendalikan ucapannya. hendaklah ia giat melakukan perbuatan-perbuatan baik melalui ucapan." },
      { number: 233, pali: "Manopakopam rakkheyya...", translation: "Hendaklah orang selalu menjaga rangsangan pikiran, hendaklah ia mengendalikan pikirannya. Setelah menghentikan perbuatan-perbuatan jahat melalui pikiran, hendaklah ia giat melakukan perbuatan-perbuatan baik melalui pikiran." },
      { number: 234, pali: "Kayena samvuta dhira...", translation: "Para bijaksana terkendali perbuatan, ucapan, dan pikirannya. Sesungguhnya mereka itu benar-benar telah dapat menguasai diri." }
    ]
  },
  {
    id: 18,
    title: "MALA VAGGA",
    translation: "Noda-Noda",
    verses: [
      { number: 235, pali: "Pandupalaso'va'dani'si...", translation: "Sekarang ini engkau bagaikan daun mengering layu. Para utusan raja kematian (Yama) telah menantimu. Engkau telah berdiri di ambang pintu keberangkatan, namun tidak kaumiliki bekal untuk perjalanan nanti." },
      { number: 236, pali: "So karohi dipam attano...", translation: "Buatlah pulau bagi dirimu sendiri Berusahalah sekarang juga dan jadikan dirimu bijaksana. Setelah membersihkan noda-noda dan bebas dari nafsu keinginan, maka engkau akan mencapai alam kedamaian para Ariya." },
      { number: 237, pali: "Upanitavayo ca'dani'si...", translation: "Sekarang kehidupanmu telah mendekati akhir, dan engkau telah mulai berjalan ke hadapan raja kematian (Yama). Tidak ada tempat bagimu berhenti di perjalanan, sedangkan engkau belum memiliki bekal untuk perjalananmu." },
      { number: 238, pali: "So karohi dipam attano...", translation: "Buatlah pulau bagi dirimu sendiri. Berusahalah sekarang juga dan jadikan dirimu bijaksana. Setelah membersihkan noda-noda dan bebas dari nafsu keinginan, maka kelahiran dan kematian tidak akan datang lagi padamu." },
      { number: 239, pali: "Anupubbena medhavi...", translation: "Dengan latihan bertahap, sedikit demi sedikit, dari waktu ke waktu hendaklah orang bijaksana membersihkan noda-noda yang ada dalam dirinya, bagaikan seorang pandai perak membersihkan perak yang berkarat." },
      { number: 240, pali: "Ayasa'va malam samutthitam...", translation: "Bagaikan karat yang timbul dari besi, bila telah timbul akan menghancurkan besi itu sendiri, begitu pula perbuatan-perbuatan sendiri yang buruk akan menjerumuskan pelakunya ke alam kehidupan yang menyedihkan." },
      { number: 241, pali: "Asajjhayamala manta...", translation: "Tidak membaca ulang adalah noda bagi mantra, tidak berusaha adalah noda bagi kehidupan berumah tangga. Kemalasan adalah noda bagi kecantikan, dan kelengahan adalah noda bagi seorang penjaga." },
      { number: 242, pali: "Mal'itthiya duccaritam...", translation: "Kelakuan buruk adalah noda bagi seorang wanita, kekikiran adalah noda bagi seorang dermawan. Sesungguhnya, segala bentuk kejahatan merupakan noda, baik dalam dunia ini maupun dalam dunia selanjutnya." },
      { number: 243, pali: "Tato mala malataram...", translation: "Yang lebih buruk dari semua noda adalah kebodohan. Kebodohan merupakan noda paling buruk. O, para bhikkhu, singkirkanlah noda ini dan hiduplah tanpa noda." },
      { number: 244, pali: "Sujivam ahirikena...", translation: "Hidup ini mudah bagi orang yang tidak tahu malu, yang suka menonjolkan diri seperti seekor burung gagak, suka menfitnah, tidak tahu sopan santun, pongah, dan menjalankan hidup kotor." },
      { number: 245, pali: "Hirimata ca dujjivam...", translation: "Hidup ini sukar bagi orang yang tahu malu, yang senantiasa mengejar kesucian, yang bebas dari kemelekatan, rendah hati, menjalankan hidup bersih dan penuh perhatian." },
      { number: 246, pali: "Yo panam atipateti...", translation: "Barang siapa membunuh makhluk hidup, suka berbicara tidak benar, mengambil apa yang tidak diberikan, merusak kesetiaan istri orang lain," },
      { number: 247, pali: "Suramerayapanam ca...", translation: "Atau menyerah pada minuman yang memabukkan; maka di dunia ini orang seperti itu bagaikan menggali kubur bagi dirinya sendiri." },
      { number: 248, pali: "Evam bho purisa janahi...", translation: "Orang baik, ketahuilah bahwa sesungguhnya tidak mudah mengendalikan hal-hal yang jahat. Jangan biarkan keserakahan dan kejahatan menyeretmu ke dalam penderitaan yang tak berkesudahan." },
      { number: 249, pali: "Dadati ve yathasraddham...", translation: "Orang-orang memberi sesuai dengan keyakinan dan menurut kesenangan hati mereka. Karena itu barang siapa yang merasa iri atas makanan dan minuman orang lain, ia tidak akan memperoleh kedamaian batin, baik siang ataupun malam." },
      { number: 250, pali: "Yassa c'etam samucchinnam...", translation: "Orang yang telah memotong perasaan iri hati ini seluruhnya, mencabut akar-akarnya serta menghancurkannya, akan memperoleh kedamaian batin, baik siang ataupun malam." },
      { number: 251, pali: "Natthi ragasamo aggi...", translation: "Tiada api yang menyamai nafsu, tiada cengkeraman yang dapat menyamai kebencian, tiada jaring yang dapat menyamai ketidaktahuan, dan tiada arus yang sederas nafsu keinginan." },
      { number: 252, pali: "Sudassam vajjama nnesam...", translation: "Amat mudah melihat kesalahan-kesalahan orang lain, tetapi sangat sulit untuk melihat kesalahan-kesalahan sendiri. Seseorang dapat menunjukkan kesalahan-kesalahan orang lain seperti menampi dedak, tetapi ia menyembunyikan kesalahan-kesalahannya sendiri seperti penjudi licik menyembunyikan dadu yang berangka buruk." },
      { number: 253, pali: "Paravajjannupasissa...", translation: "Barang siapa yang selalu memperhatikan dan mencari-cari kesalahan orang lain, maka kekotoran batin dalam dirinya akan bertambah dan ia semakin jauh dari penghancuran kekotoran-kekotoran batin." },
      { number: 254, pali: "Akase va padam natthi...", translation: "Tidak ada jejak di angkasa, tidak ada orang suci di luar Dhamma. Umat manusia bergembira di dalam belenggu, tetapi Para Tathagata telah bebas dari semua itu." },
      { number: 255, pali: "Akase va padam natthi...", translation: "Tidak ada jejak di angkasa, tidak ada orang suci di luar Dhamma. Tidak ada hal-hal berkondisi yang abadi. Tidak ada lagi keragu-raguan bagi Para Buddha." }
    ]
  },
  {
    id: 19,
    title: "DHAMMATTHA VAGGA",
    translation: "Orang Adil",
    verses: [
      { number: 256, pali: "Na tena hoti dhammattho...", translation: "Orang yang memutuskan segala sesuatu dengan tergesa-gesa tidak dapat dikatakan sebagai orang adil Orang bijaksana hendaknya memeriksa dengan teliti mana yang benar dan mana yang salah." },
      { number: 257, pali: "Yo nayena nayati pare...", translation: "Orang yang mengadili orang lain dengan tidak tergesa-gesa, bersikap adil dan tidak berat sebelah, yang senantiasa menjaga kebenaran, pantas disebut orang adil." },
      { number: 258, pali: "Na tena pandito hoti...", translation: "Seseorang tidak dapat dikatakan bijaksana hanya karena ia banyak bicara. tetapi orang yang damai, tanpa rasa benci dan rasa takut dapat disebut orang bijaksana." },
      { number: 259, pali: "Na tavata dhammadharo...", translation: "Seseorang bukan ‘pendukung Dhamma’ hanya karena ia banyak bicara. Namun seseorang yang walaupun hanya belajar sedikit tetapi batinnya melihat Dhamma dan tidak melalaikannya, maka sesungguhnya ia adalah seorang ‘pendukung Dhamma’" },
      { number: 260, pali: "Na tena thero hoti...", translation: "Seseorang tidak disebut “Thera (lebih tua)” hanya karena rambutnya telah memutih. Biarpun usianya sudah lanjut, dapat saja ia disebut ‘orang tua yang tidak berguna’." },
      { number: 261, pali: "Yamhi saccam ca dhammo ca...", translation: "Orang yang memiliki kebenaran dan kebajikan, tidak kejam, terkendali dan terlatih, pandai dan bebas dari noda-noda, sesungguhnya ia patut disebut Thera." },
      { number: 262, pali: "Na vakkaranamattena...", translation: "Bukan hanya karena pandai bicara dan bukan pula karena memiliki penampilan yang baik seseorang dapat menyebut dirinya orang yang baik hati, apabila ia masih bersifat iri, kikir dan suka menipu." },
      { number: 263, pali: "Yassa c'etam samucchinnam...", translation: "Orang yang telah memotong, mencabut dan memutuskan akar sifat iri hati, kekikiran serta dusta; maka orang bijaksana yang telah menyingkirkan segala keburukan itulah sesungguhnya dapat disebut orang yang baik hati." },
      { number: 264, pali: "Na mundakena samano...", translation: "Seseorang yang tidak memiliki disiplin dan suka berdusta tidak dapat disebut seorang pertapa (samana) walaupun ia berkepala gundul. Mana mungkin orang yang penuh dengan keinginan serta keserakahan dapat menjadi seorang samana?" },
      { number: 265, pali: "Yo ca sameti papani...", translation: "Barang siapa dapat mengalahkan semua kejahatan, baik yang kecil maupun yang besar, ia patut disebut seorang samana karena ia telah mengatasi semua kejahatan." },
      { number: 266, pali: "Na tena bhikkhu hoti...", translation: "Seseorang tidak dapat disebut bhikkhu hanya karena ia mengumpulkan dana makanan dari orang lain. Selama ia masih bertingkah laku seperti seorang perumah tangga dan tidak mentaati peraturan, maka ia belum pantas disebut bhikkhu." },
      { number: 267, pali: "Yo'dha punnan ca papan ca...", translation: "Dalam hal ini, seseorang yang telah mengatasi kebaikan dan kejahatan, yang menjalankan kehidupan suci dan melaksanakan perenungan tentang kelompok-kelompok khandha, maka sesungguhnya ia dapat disebut seorang bhikkhu." },
      { number: 268, pali: "Na monena muni hoti...", translation: "Tidak hanya karena berdiam diri seorang menjadi orang suci (muni), apabila ia dungu dan bodoh. bagaikan memegang sepasang neraca, orang bijaksana melaksanakan sesuatu yang baik dan menghindari yang jahat." },
      { number: 269, pali: "Yo munati ubho loke...", translation: "Karena seseorang dapat memilih apa yang baik dan menghindari apa yang buruk, maka ia disebut sebagai orang suci. Demikianlah, ia yang telah mengerti kedua kelompok (batin maupun jasmani), patut disebut orang suci." },
      { number: 270, pali: "Na tena ariyo hoti...", translation: "Seseorang tidak dapat disebut Ariya (orang mulia) apabila masih menyiksa makhluk hidup. Ia yang tidak lagi menyiksa makhluk-makhluk hiduplah yang dapat dikatakan mulia." },
      { number: 271, pali: "Na silabbatamattena...", translation: "Bukan hanya karena sila dan tekad, bukan pula karena banyak belajar ataupun karena telah mencapai perkembangan dalam samadhi, atau juga karena berdiam diri di tempat yang sepi;" },
      { number: 272, pali: "Bhikkhu vissasamapadi...", translation: "Lalu berpikir: ‘Aku telah menikmati kebahagiaan dari pelepasan yang tidak dapat dicapai oleh orang duniawi.’ O para bhikkhu, janganlah engkau merasa puas sebelum mencapai penghancuran semua kekotoran batin." }
    ]
  },
  {
    id: 20,
    title: "MAGGA VAGGA",
    translation: "Jalan",
    verses: [
      { number: 273, pali: "Maggan'atthangiko settho...", translation: "Di antara semua jalan, maka “Jalan Mulia Berfaktor Delapan’ adalah yang terbaik; di antara semua kebenaran, maka ‘Empat Kebenaran Mulia’ adalah yang terbaik. Di antara semua keadaan, maka keadaan tanpa nafsu adalah yang terbaik; dan di antara semua makhluk hidup, maka orang yang ‘melihat’ adalah yang terbaik." },
      { number: 274, pali: "Eso-va maggo natth'anno...", translation: "Inilah satu-satunya ‘Jalan’. Tidak ada jalan lain yang dapat membawa pada kemurnian pandangan. Ikutilah jalan ini, yang dapat mengalahkan Mara (penggoda)." },
      { number: 275, pali: "Etam hi tumhe patipanna...", translation: "Dengan mengikuti ‘Jalan’ ini, engkau dapat mengakhiri penderitaan. Dan jalan ini pula yang Kutunjukkan setelah Aku mengetahui bagaimana cara mencabut duri-duri (kekotoran batin)." },
      { number: 276, pali: "Tumhehi kiccam atappam...", translation: "Engkau sendirilah yang harus berusaha, para Tathagata hanya menunjukkan ‘Jalan’. Mereka yang tekun bersemadi dan memasuki ‘Jalan’ ini akan terbebas dari belenggu Mara." },
      { number: 277, pali: "Sabbe sankhara anicca...", translation: "Segala sesuatu yang berkondisi tidak kekal adanya. Apabila dengan kebijaksanaan orang dapat melihat hal ini; maka ia akan merasa jemu dengan penderitaan. Inilah Jalan yang membawa pada kesucian." },
      { number: 278, pali: "Sabbe sankhara dukkha...", translation: "Segala sesuatu yang berkondisi adalah dukkha. Apabila dengan kebijaksanaan orang dapat melihat hal ini, maka ia akan merasa jemu dengan penderitaan. Inilah Jalan yang membawa pada kesucian." },
      { number: 279, pali: "Sabbe dhamma anatta...", translation: "Segala sesuatu yang berkondisi adalah tanpa inti. Apabila dengan kebijaksanaan orang dapat melihat ini, maka ia akan merasa jemu dengan penderitaan. Inilah Jalan yang membawa pada kesucian." },
      { number: 280, pali: "Utthanakalamhi anutthahano...", translation: "Walaupun seseorang masih muda dan kuat, namun bila ia malas dan tidak mau berjuang semasa harus berjuang, serta berpikiran lamban; maka orang yang malas dan lamban seperti itu tidak akan menemukan Jalan yang mengantarnya pada kebijaksanaan." },
      { number: 281, pali: "Vacannurakkhi manasa susamvuto...", translation: "Hendaklah ia menjaga ucapan dan mengendalikan pikiran dengan baik serta tidak melakukan perbuatan jahat melalui jasmani. Hendaklah ia memurnikan tiga saluran perbuatan ini, memenangkan ‘Jalan’ yang telah dibabarkan oleh Para Suci." },
      { number: 282, pali: "Yoga ve jayati bhuri...", translation: "Sesungguhnya dari meditasi akan timbul kebijaksanaan; tanpa meditasi kebijaksanaan akan pudar. Setelah mengetahui kedua jalan bagi perkembangan dan kemerosotan batin, hendaklah orang melatih diri sehingga kebijaksanaannya berkembang." },
      { number: 283, pali: "Vanam chindatha ma rukkham...", translation: "O, Para bhikkhu, tebanglah hutan nafsu itu, karena dari nafsu timbul ketakutan. Setelah menebang hutan dan belukar nafsu, jadilah orang yang tidak lagi memiliki nafsu." },
      { number: 284, pali: "Yavam hi vanatho na chijjati...", translation: "Selama nafsu keinginan laki-laki terhadap wanita belum dihancurkan, betapapun kecilnya, maka selama itu pula seseorang masih terikat pada kehidupan, bagaikan seekor anak sapi yang masih menyusu pada induknya." },
      { number: 285, pali: "Ucchinda sineham attano...", translation: "Patahkanlah rasa cinta terhadap diri sendiri, seperti memetik bunga teratai putih di musim gugur. Kembangkanlah jalan kedamaian Nibbana yang telah diajarkan oleh Sang Sugata." },
      { number: 286, pali: "Idha vassam vasissami...", translation: "Di sini aku akan berdiam pada musim hujan, di sini aku akan berdiam selama musim gugur dan musim panas. Demikianlah pikiran orang bodoh yang tidak menyadari bahaya (kematian)." },
      { number: 287, pali: "Tam puttapasusammatam...", translation: "Orang yang pikirannya melekat pada anak-anak dan ternak peliharaannya, maka kematian akan menyeret dan menghanyutkannya, seperti banjir besar yang menghanyutkan sebuah desa yang tertidur." },
      { number: 288, pali: "Na santi putta tanaya...", translation: "Anak-anak tidak dapat melindungi, begitu juga ayah maupun sanak saudara. Bagi orang yang sedang menghadapi kematian, maka tidak ada sanak saudara yang dapat melindungi dirinya lagi." },
      { number: 289, pali: "Etam-atthavasam natva...", translation: "Setelah mengetahui kenyataan ini, Maka orang berbudi dan bijaksana tak akan menunda waktu dalam menempuh jalan menuju Nibbana." }
    ]
  },
  {
    id: 21,
    title: "PAKINNAKA VAGGA",
    translation: "Bunga Rampai",
    verses: [
      { number: 290, pali: "Mattasukhapariccaga...", translation: "Apabila dengan melepaskan kebahagiaan yang lebih kecil orang dapat memperoleh kebahagiaan yang lebih besar, maka hendaknya orang bijaksana melepaskan kebahagiaan yang kecil itu, guna memperoleh kebahagiaan yang lebih besar." },
      { number: 291, pali: "Paradukkhupadanena...", translation: "Barangsiapa menginginkan kebahagiaan bagi dirinya sendiri dengan menimbulkan penderitaan orang lain, maka ia tidak akan terbebas dari kebencian; ia akan terjerat dalam kebencian." },
      { number: 292, pali: "Yam hi kiccam tadapaviddham...", translation: "Orang yang melakukan yang seharusnya tak dilakukan dan tidak melakukan apa yang seharusnya dilakukan, maka kekotoran batin akan terus bertambah dalam diri orang yang sombong dan malas seperti itu." },
      { number: 293, pali: "Yesan ca susamaraddha...", translation: "Mereka yang selalu giat melatih perenungan terhadap badan jasmani, tidak melakukan apa yang seharusnya tak dilakukan, dan melakukan apa yang seharusnya dilakukan, maka kekotoran-kekotoran batin akan lenyap dari diri mereka yang memiliki kesadaran dan pandangan terang seperti itu." },
      { number: 294, pali: "Mataram pitaram hantva...", translation: "Setelah membantai ibu (nafsu keinginan) dan ayah (kesombongan), serta dua orang ksatria; dan setelah menghancurkan negara (pintu-pintu indria) bersama dengan para menterinya (kemelekatan), maka seorang brahmana akan berjalan pergi tanpa kesedihan." },
      { number: 295, pali: "Mataram pitaram hantva...", translation: "Setelah membantai ibu (nafsu keinginan) dan ayah (kesombongan), serta dua raja yang arif; dan setelah menghancurkan lima jalan yang penuh bahaya (lima rintangan batin), maka seorang brahmana akan berjalan pergi tanpa kesedihan." },
      { number: 296, pali: "Suppabuddham pabujjhanti...", translation: "Para siswa Gotama telah bangun dengan baik dan selalu sadar, sepanjang siang dan malam mereka selalu merenungkan sifat-sifat mulia Sang Buddha dengan penuh kesadaran." },
      { number: 297, pali: "Suppabuddham pabujjhanti...", translation: "Para siswa Gotama telah bangun dengan baik dan selalu sadar, sepanjang siang dan malam mereka selalu merenungkan sifat-sifat mulia Dhamma dengan penuh kesadaran." },
      { number: 298, pali: "Suppabuddham pabujjhanti...", translation: "Para siswa Gotama telah bangun dengan baik dan selalu sadar, sepanjang siang dan malam mereka selalu merenungkan sifat-sifat mulia Sangha dengan penuh kesadaran." },
      { number: 299, pali: "Suppabuddham pabujjhanti...", translation: "Para siswa Gotama telah bangun dengan baik dan selalu sadar, sepanjang siang dan malam mereka selalu merenungkan sifat-sifat badan jasmani dengan penuh kesadaran." },
      { number: 300, pali: "Suppabuddham pabujjhanti...", translation: "Para siswa Gotama telah bangun dengan baik dan selalu sadar, sepanjang siang dan malam mereka bergembira dalam keadaan bebas dari kekejaman." },
      { number: 301, pali: "Suppabuddham pabujjhanti...", translation: "Para siswa Gotama telah bangun dengan baik dan selalu sadar, sepanjang siang dan malam mereka bergembira dalam ketentraman samadhi." },
      { number: 302, pali: "Duppabbajjam durabhiramam...", translation: "Sungguh sukar untuk menempuh kehidupan tanpa rumah (Pabbajja); sungguh sukar untuk bergembira dalam menempuh kehidupan tanpa rumah. Kehidupan rumah tangga adalah sukar dan menyakitkan. Tinggal bersama mereka yang tidak sesuai sungguh menyakitkan. Hidup mengembara dalam proses tumimbal lahir (Samsara) juga menyakitkan." },
      { number: 303, pali: "Saddho silena sampanno...", translation: "Bagi orang yang memiliki keyakinan dan sila yang sempurna, akan memperoleh nama harum dan kekayaan, pergi ketempat manapun ia akan dihormati." },
      { number: 304, pali: "Dure santo pakasanti...", translation: "Meskipun dari jauh, orang baik akan terlihat bersinar bagaikan puncak pegunungan Himalaya. Tetapi, meskipun dekat, orang jahat tidak akan terlihat, bagaikan anak panah yang dilepaskan pada malam hari." },
      { number: 305, pali: "Ekasanamm ekaseyyam...", translation: "Ia yang duduk sendiri, tidur sendiri, berjalan sendiri tanpa rasa jemu serta selalu membina diri, akan bergembira di dalam hutan." }
    ]
  },
  {
    id: 22,
    title: "NIRAYA VAGGA",
    translation: "Neraka",
    verses: [
      { number: 306, pali: "Abhutavadi nirayam upeti...", translation: "Orang yang selalu berbicara tidak benar dan juga orang yang setelah berbuat kemudian berkata, “Aku tidak melakukannya” akan masuk ke neraka. Dua macam orang yang mempunyai kelakuan rendah ini, mempunyai nasib yang sama dalam dunia selanjutnya." },
      { number: 307, pali: "Kasavakantha bahavo...", translation: "Bila seseorang menjadi bhikkhu dengan mengenakan jubah kuning tetapi masih berkelakuan buruk dan tidak terkendali, maka akibat perbuatan-perbuatan jahatnya sendiri, ia akan masuk ke alam neraka." },
      { number: 308, pali: "Seyyo ayogulo bhutto...", translation: "Lebih baik menelan bola besi panas seperti bara api daripada selalu menerima makanan dari orang lain dan tetap berkelakuan buruk serta tak terkendali." },
      { number: 309, pali: "Cattari thanani naro pamatto...", translation: "Orang yang lengah dan berzina akan menerima empat ganjaran, yaitu : pertama, ia akan menerima akibat buruk; kedua, ia tidak dapat tidur dengan tenang; ketiga, namanya tercela; dan keempat, ia akan masuk ke alam neraka." },
      { number: 310, pali: "Apunnalabho ca gati ca papika...", translation: "Ia akan menerima akibat buruk dan kelahiran rendah pada kehidupannya yang akan datang. Sungguh singkat kenikmatan yang diperoleh lelaki dan wanita yang ketakutan, dan rajapun akan menjatuhkan hukuman berat." },
      { number: 311, pali: "Kuso yatha duggahito...", translation: "Bagaikan rumput kusa, bila dipegang secara salah akan melukai tangan; begitu juga kehidupan seorang pertapa, apabila dijalankan secara salah akan menyeret orang ke neraka." },
      { number: 312, pali: "Yam kinci sithilam kammam...", translation: "Bila suatu pekerjaan dikerjakan dengan seenaknya, suatu tekad tidak dijalankan dengan selayaknya, kehidupan suci tidak dijalankan dengan sepenuh hati; maka semuanya ini tidak akan membuahkan hasil yang besar." },
      { number: 313, pali: "Kayam ce kayiratha-nam...", translation: "Hendaklah orang mengerjakan sesuatu dengan sepenuh hati. Suatu kehidupan suci yang dijalankan dengan seenaknya akan membangkitkan debu nafsu yang lebih besar." },
      { number: 314, pali: "Akatam dukkatam seyyo...", translation: "Sebaiknya seseorang tidak melakukan perbuatan jahat, karena di kemudian hari perbuatan itu akan menyiksa dirinya sendiri. Lebih baik seseorang melakukan perbuatan baik, karena setelah melakukannya ia tidak akan menyesal." },
      { number: 315, pali: "Nagaram yatha paccantam...", translation: "Bagaikan perbatasan negara yang dijaga kuat di bagian dalam dan luar, begitu pula seharusnya engkau menjaga dirimu; janganlah membiarkan kesempatan baik (dalam era ajaran Sang Buddha) ini berlalu." },
      { number: 316, pali: "Alajjitaye lajjanti...", translation: "Mereka yang merasa malu terhadap apa yang sebenarnya tidak memalukan, dan sebaliknya tidak merasa malu terhadap apa yang sebenarnya memalukan; maka orang yang menganut pandangan salah seperti itu akan masuk ke alam sengsara." },
      { number: 317, pali: "Abhaye bhayadassino...", translation: "Mereka yang merasa takut terhadap apa yang sebenarnya tidak menakutkan, dan sebaliknya tidak merasa takut terhadap apa yang sebenarnya menakutkan; maka orang yang menganut pandangan salah seperti itu akan masuk ke alam sengsara." },
      { number: 318, pali: "Avajje vajjadassino...", translation: "Mereka yang menganggap tercela terhadap apa yang sebenarnya tidak tercela, dan menganggap tidak tercela terhadap apa yang sebenarnya tercela; maka orang yang menganut pandangan salah seperti itu akan masuk ke alam sengsara." },
      { number: 319, pali: "Vajjan ca vajjato natva...", translation: "Mereka yang mengetahui apa yang tercela sebagai tercela, dan apa yang tidak tercela sebagai tidak tercela; maka orang yang menganut pandangan benar seperti itu akan masuk ke alam bahagia." }
    ]
  },
  {
    id: 23,
    title: "NAGA VAGGA",
    translation: "Gajah",
    verses: [
      { number: 320, pali: "Aham nago-va samgame...", translation: "Seperti seekor gajah di medan perang dapat menahan serangan panah yang dilepaskan dari busur, begitu pula Aku (Tathagata) tetap bersabar terhadap cacian; sesungguhnya, sebagian besar orang mempunyai kelakuan rendah." },
      { number: 321, pali: "Dantam nayanti samitim...", translation: "Mereka menuntun gajah yang telah terlatih ke hadapan orang banyak. Raja mengendarai gajah yang terlatih ke medan perang. Di antara umat manusia, maka yang terbaik adalah orang yang dapat menaklukkan dirinya sendiri dan dapat bersabar terhadap cacian." },
      { number: 322, pali: "Varam assatara danta...", translation: "Sungguh baik keledai-keledai yang terlatih, begitu juga kuda-kuda Sindhu dan gajah-gajah perang milik para bangsawan; tetapi yang jauh lebih baik dari semua itu adalah orang yang telah dapat menaklukkan dirinya sendiri." },
      { number: 323, pali: "Na hi etehi yanehi...", translation: "Tidak dengan mengendarai tunggangan seperti itu seseorang dapat pergi ke tempat yang belum pernah didatangi (nibbana). Namun orang yang telah dapat melatih, menaklukkan, dan mengendalikan dirinya sendiri dapat pergi ke tempat yang belum pernah didatangi itu (nibbana)." },
      { number: 324, pali: "Dhanapalako nama kunjaro...", translation: "Pada musim kawin, gajah ganas bernama Dhanapalaka sukar dikendalikan; walaupun diikat kuat ia tetap tidak mau makan karena merindukan gajah-gajah lain di hutan." },
      { number: 325, pali: "Middhi yada hoti mahagghaso...", translation: "Jika seseorang menjadi malas, serakah, rakus akan makanan dan suka merebahkan diri seperti babi hutan yang berguling-guling ke sana kemari. Orang yang bodoh ini akan terus menerus dilahirkan." },
      { number: 326, pali: "Idam pure cittam acari carikam...", translation: "Dahulu pikiran ini mengembara, pergi kepada objek-objek yang disukai, dingini, dan ke mana yang dikehendaki. Sekarang aku akan mengendalikannya dengan penuh perhatian, seperti penjinak gajah mengendalikan gajah dengan kaitan besi." },
      { number: 327, pali: "Appamada rata hotha...", translation: "Bergembiralah dalam kewaspadaan dan jagalah pikiranmu dengan baik; bebaskanlah dari cara-cara yang salah, seperti seekor gajah melepaskan dirinya yang terbenam dalam lumpur." },
      { number: 328, pali: "Sace labhetha nipakam sahayum...", translation: "Apabila dalam pengembaraanmu engkau dapat menemukan seorang sahabat yang berkelakuan baik, pandai, dan bijaksana, maka hendaknya engkau berjalan bersamanya dengan senang hati dan penuh kesadaran untuk mengatasi semua bahaya." },
      { number: 329, pali: "No ce labhetha nipakam sahayum...", translation: "Apabila dalam pengembaraanmu engkau tak dapat menemukan seorang sahabat yang berkelakukan baik, pandai, dan bijaksana, maka hendaknya engkau berjalan seorang diri, seperti seorang raja yang meninggalkan negara yang telah dikalahkannya, atau seperti seekor gajah yang mengembara sendiri di dalam hutan." },
      { number: 330, pali: "Ekassa caritam seyyo...", translation: "Lebih baik mengembara seorang diri dan tidak bergaul dengan orang bodoh. Pergilah seorang diri dan jangan berbuat jahat, hiduplah dengan bebas (tidak banyak kebutuhan), seperti seekor gajah yang mengembara sendiri di dalam hutan." },
      { number: 331, pali: "Atthamhi jatamhi sukha sahayu...", translation: "Sungguh bahagia mempunyai kawan pada saat kita membutuhkannya; sungguh bahagia dapat merasa puas dengan apa yang diperoleh; sungguh bahagia dapat berbuat kebaikan menjelang kematian; dan sungguh bahagia dapat mengakhiri penderitaan." },
      { number: 332, pali: "Sukha matteyyata loke...", translation: "Berlaku baik terhadap ibu berlaku baik terhadap ayah juga merupakan kebahagiaan. Berlaku baik terhadap pertapa merupakan suatu kebahagiaan dalam dunia ini; berlaku baik terhadap para Ariya juga merupakan kebahagiaan." },
      { number: 333, pali: "Sukham yavam jara sila...", translation: "Moral (Sila) akan memberikan kebahagiaan sampai usia tua; keyakinan yang telah ditanam kuat akan memberikan kebahagiaan; kebijaksanaan yang telah diperoleh akan memberikan kebahagiaan; tidak berbuat jahat akan memberikan kebahagiaan." }
    ]
  },
  {
    id: 24,
    title: "TANHA VAGGA",
    translation: "Nafsu Keinginan",
    verses: [
      { number: 334, pali: "Manujassa pamattacarino...", translation: "Bila seseorang hidup lengah, maka nafsu keinginan tumbuh, seperti tanaman Maluwa yang menjalar. Ia melompat dari satu kehidupan ke kehidupan yang lain, bagaikan kera yang senang mencari buah-buahan di dalam hutan." },
      { number: 335, pali: "Yam esa sahati jammi...", translation: "Dalam dunia ini, siapapun yang dikuasai oleh nafsu keinginan rendah dan beracun, penderitaannya akan bertambah seperti rumput Birana yang tumbuh dengan cepat karena disirami dengan baik." },
      { number: 336, pali: "Yo c'etam sahati jammim...", translation: "Tetapi barang siapa dapat mengatasi nafsu keinginan yang beracun dan sukar dikalahkan itu, maka kesedihan akan berlalu dari dalam dirinya, seperti air yang jatuh dari daun teratai." },
      { number: 337, pali: "Tam vo vadami bhaddam vo...", translation: "Kuberitahukan hal ini kepadamu: Semoga engkau sekalian yang telah datang berkumpul di sini memperoleh kesejahteraan! Bongkarlah nafsu keinginanmu, seperti orang mencabut akar rumput Birana yang harum. Jangan biarkan Mara menghancurkan dirimu berulang kali." },
      { number: 338, pali: "Yathapi mule anupaddave dalhe...", translation: "Sebatang pohon yang telah ditebang masih akan dapat tumbuh dan bersemi lagi apabila akar-akarnya masih kuat dan tidak dihancurkan. Begitu pula selama akar nafsu keinginan tidak dihancurkan, maka penderitaan akan tumbuh berulang kali." },
      { number: 339, pali: "Yassa chattrimsati sota...", translation: "Apabila tiga puluh enam nafsu keinginan di dalam diri seseorang mengalir deras menuju objek-objek yang menyenangkan, maka gelombang pikiran yang penuh nafsu akan menyeret orang yang memiliki pandangan salah seperti itu." },
      { number: 340, pali: "Savanti sabbadhi sota...", translation: "Di mana-mana mengalir arus (=nafsu-nafsu keinginan); di mana-mana tanaman menjalar tumbuh merambat. Apabila engkau melihat tanaman menjalar (=nafsu keinginan) tumbuh tinggi, maka harus kau potong akar-akarnya dengan pisau (=kebijaksanaan)." },
      { number: 341, pali: "Saritani sinehitani ca...", translation: "Dalam diri makhluk-makhluk timbul rasa senang mengejar objek-objek indria, dan mereka menjadi terikat pada keinginan-keinginan indria. Karena cenderung pada hal-hal yang menyenangkan dan terus mengejar kenikmatan-kenikmatan indria, maka mereka menjadi korban kelahiran dan kelapukan." },
      { number: 342, pali: "Tasina purakkkhata paja...", translation: "Makhluk-makhluk yang terikat pada nafsu keinginan, berlarian kian kemari seperti seekor kelinci yang terjebak. Karena terikat erat oleh belenggu-belenggu dan ikatan-ikatan, maka mereka mengalami penderitaan untuk waktu yang lama." },
      { number: 343, pali: "Tasina purakkhata paja...", translation: "Makhluk-makhluk yang terikat oleh nafsu-nafsu keinginan, berlarian kian kemari seperti seekor kelinci yang terjebak. Karena itu seorang bhikkhu yang menginginkan kebebasan diri, hendaknya ia membuang segala nafsu-nafsu keinginannya." },
      { number: 344, pali: "Yo nibbanatho vanadhimutto...", translation: "Setelah bebas dari hutan keinginan (=kehidupan rumah tangga), ia menemukan hutan kesucian (=kehidupan pertapa). Tapi, walaupun telah bebas dari keinginan (akan kehidupan rumah tangga) ia kembali ke rumah lagi." },
      { number: 345, pali: "Na tam dalham bandhanam ahu dhira...", translation: "Orang bijaksana menyatakan bahwa belenggu yang terbuat dari besi, kayu, ataupun rami tidaklah begitu kuat. Tetapi ikatan terhadap anak-anak, istri, dan harta benda, sesungguhnya merupakan belenggu yang jauh lebih kuat." },
      { number: 346, pali: "Etam dalham bandhanam ahu dhira...", translation: "Orang bijaksana menyatakan bahwa belenggu seperti itu amat kuat, dapat melemparkan orang ke bawah, halus dan sukar untuk dilepaskan. walaupun demikian, para bijaksana akan dapat memutuskan belenggu itu." },
      { number: 347, pali: "Ye raga-ratta'nupatanti sotam...", translation: "Mereka yang bergembira dengan nafsu indria, akan jatuh ke dalam arus (kehidupan), seperti laba-laba yang jatuh ke dalam jaring yang dibuatnya sendiri. Tapi para bijaksana dapat memutuskan belenggu itu." },
      { number: 348, pali: "Munca pure munca pacchato...", translation: "Tinggalkan apa yang telah lalu, yang akan datang maupun sekarang (=kemelekatan terhadap lima kelompok kehidupan) dan capailah ‘Pantai Seberang’ (=nibbana). Dengan pikiran yang telah bebas dari segala sesuatu, maka engkau tak akan mengalami kelahiran dan kelapukan lagi." },
      { number: 349, pali: "Vitakkapamathitassa jantuno...", translation: "Orang yang pikirannya kacau, penuh dengan nafsu, dan hanya melihat pada hal-hal yang menyenangkan saja, maka nafsu keinginannya akan terus bertambah. Sesungguhnya orang seperti itu hanya akan memperkuat ikatan belenggunya sendiri ." },
      { number: 350, pali: "Vitakkupasame ca yo rato...", translation: "Orang yang bergembira dalam menenangkan pikirannya, tekun merenungkan hal-hal yang menjijikkan (sebagai objek perenungan dalam samadhi) dan selalu sadar, maka ia akan mengakhiri nafsu-nafsu keinginannya dan menghancurkan belenggu Mara." },
      { number: 351, pali: "Nitthangato asantasi...", translation: "Orang yang telah mencapai tujuan akhir, tidak lagi mempunyai rasa takut, noda batin serta nafsu keinginan, sesungguhnyalah ia telah mematahkan ruji-ruji kehidupan. Bagi orang suci seperti itu, tubuhnya merupakan tubuh yang terakhir." },
      { number: 352, pali: "Vitatanho anadano...", translation: "Orang yang telah bebas dari nafsu keinginan dan kemelekatan, pandai dalam menganalisa serta memahami ‘Ajaran’ beserta pasangan-pasangannya, maka ia patut disebut seorang ‘Pemilik Tubuh Terakhir’ (=arahat)." },
      { number: 353, pali: "Sabbabhibhu sabbavidu'ham asmi...", translation: "Aku telah mengalahkan semuanya. Aku telah mengetahui semuanya. Aku telah bebas dari semuanya. Aku telah meninggalkan semuanya. Setelah menghancurkan nafsu keinginan, Aku benar-benar bebas." },
      { number: 354, pali: "Sabbadanam dhammadanam jinati...", translation: "Pemberian ‘Kebenaran’ (Dhamma) mengalahkan semua pemberian lainnya; rasa ‘Kebenaran’ (Dhamma) mengalahkan semua rasa lainnya; kegembiraan dalam ‘Kebenaran’ (Dhamma) mengalahkan semua kegembiraan lainnya." },
      { number: 355, pali: "Hananti bhoga dummedham...", translation: "Kekayaan dapat menghancurkan orang bodoh, tetapi tidak menghancurkan mereka yang mencari ‘Pantai Seberang’ (=nibbana). Karena nafsu keinginan mendapatkan kekayaan, orang bodoh menghancurkan dirinya sendiri." },
      { number: 356, pali: "Tinadosani khettani...", translation: "Rumput liar merupakan bencana bagi sawah dan ladang; nafsu indria merupakan bencana bagi manusia. Karena itu dana yang dipersembahkan kepada mereka yang telah bebas dari nafsu indria akan menghasilkan pahala yang besar." },
      { number: 357, pali: "Tinadosani khettani...", translation: "Rumput liar merupakan bencana bagi sawah dan ladang; kebencian merupakan bencana bagi manusia. karena itu, dana yang dipersembahkan kepada mereka yang telah bebas dari kebencian akan menghasilkan pahala yang besar." },
      { number: 358, pali: "Tinadosani khettani...", translation: "Rumput liar merupakan bencana bagi sawah dan ladang; ketidak-tahuan merupakan bencana bagi manusia. Karena itu, dana yang dipersembahkan kepada mereka yang telah bebas dari ketidak-tahuan akan menghasilkan pahala yang besar." },
      { number: 359, pali: "Tinadosani khettani...", translation: "Rumput liar merupakan bencana bagi sawah dan ladang; iri hati merupakan bencana bagi mannusia. Karena itu, dana yang dipersembahkan kepada mereka yang telah bebas dari iri hati akan menghasilkan pahala yang besar." }
    ]
  },
  {
    id: 25,
    title: "BHIKKHU VAGGA",
    translation: "Bhikkhu",
    verses: [
      { number: 360, pali: "Cakkhuna samvaro sadhu...", translation: "Sungguh baik mengendalikan mata; sungguh baik mengendalikan telinga; sungguh baik mengendalikan hidung; sungguh baik mengendalikan lidah." },
      { number: 361, pali: "Kayena samvaro sadhu...", translation: "Sungguh baik mengendalikan perbuatan; sungguh baik mengendalikan ucapan; sungguh baik mengendalikan pikiran; Seorang bhikkhu yang dapat mengendalikan semuanya akan terbebas dari semua penderitaan." },
      { number: 362, pali: "Hatthasamyato padasamyato...", translation: "Seseorang yang mengendalikan tangan dan kakinya, ucapan dan pikirannya, yang bergembira dalam samadhi dan memiliki batin yang tenang, yang puas berdiam seorang diri, maka orang lain menamakan dia seorang “bhikkhu”." },
      { number: 363, pali: "Yo mukhasamyato bhikkhu...", translation: "Seorang bhikkhu yang mengendalikan lidahnya, yang berbicara dengan bijaksana dan tidak sombong, yang dapat menerangkan Dhamma beserta artinya, maka ia akan kedengaran indah ucapannya." },
      { number: 364, pali: "Dhammaramo dhammarato...", translation: "Seorang bhikkhu yang selalu berdiam dalam Dhamma dan bergembira dalam Dhamma, yang selalu merenungkan dan mengingat-ingat akan Dhamma, maka bhikkhu itu tidak akan tergelincir dari Jalan Benar Yang Mulia." },
      { number: 365, pali: "Salabham natimanneyya...", translation: "Hendaklah ia tidak mencela apa-apa yang telah ia peroleh, juga hendaklah ia tidak merasa iri terhadap apa yang telah diperoleh orang lain." },
      { number: 366, pali: "Appalābho pi ce bhikkhu...", translation: "Walaupun hanya memperoleh sedikit, tetapi apabila seseorang bhikkhu tidak mencela apa yang telah diperolehnya, maka para dewa pun akan memuji orang seperti itu, yang memiliki kehidupan bersih serta tidak malas." },
      { number: 367, pali: "Sabbaso namarupasmin...", translation: "Apabila seseorang tidak lagi melekat pada konsepsi “aku” atau “milikku”, baik yang berkenaan dengan batin maupun jasmani, dan tidak bersedih terhadap apa yang tidak dimilikinya, maka orang seperti itu layak disebut bhikkhu." },
      { number: 368, pali: "Mettavihari yo bhikkhu...", translation: "Apabila seorang bhikkhu hidup dalam cinta kasih, dan memiliki keyakinan terhadap ajaran Sang Buddha, maka ia akan sampai pada keadaan damai (nibbana), yang merupakan berhentinya hal-hal yang berkondisi (sankhara)." },
      { number: 369, pali: "Sinca bhikkhu imam navam...", translation: "O bhikkhu, kosongkanlah perahu (tubuh) ini. Apabila telah dikosongkan maka perahu ini akan melaju pesat. Setelah memutuskan nafsu keinginan dan kebencian, maka engkau akan mencapai nibbana." },
      { number: 370, pali: "Panca chinde panca jahe...", translation: "Putuskanlah lima kelompok belenggu pertama, dan singkirkanlah lima kelompok kedua. Serta kembangkan lagi lima kekuatan secara sempurna. Apabila seorang bhikkhu telah bebas dari lima belenggu, maka ia disebut seorang ‘Penyeberang Arus’ (sotapanna)." },
      { number: 371, pali: "Jhaya bhikkhu ma ca pamado...", translation: "Bersamadhilah, O bhikkhu! Jangan lengah ! Jangan biarkan pikiranmu diseret oleh kesenangan-kesenangan indria! Jangan karena lengah maka engkau harus menelan bola besi yang membara!" },
      { number: 372, pali: "Natthi jhanam apannassa...", translation: "Tak ada samadhi dalam diri orang yang tidak memiliki kebijaksanaan. Dan tidak ada kebijaksanaan dalam diri orang yang tidak bersamadhi. Orang yang memiliki samadhi dan kebijaksanaan, sesungguhnya sudah berada di ambang pintu nibbana." },
      { number: 373, pali: "Sunnagaram pavitthassa...", translation: "Apabila seorang bhikkhu pergi ke tempat sepi, telah menenangkan pikirannya, dan telah dapat melihat Dhamma dengan jelas, akan merasakan kegembiraan yang belum pernah dirasakan oleh orang-orang biasa." },
      { number: 374, pali: "Yato yato sammasati...", translation: "Bila seorang dapat melihat dengan jelas akan timbul dan lenyapnya kelompok kehidupan ( = khandha), maka ia akan merasakan kegembiraan dan ketentraman batin. Sesungguhnya, bagi mereka yang telah mengerti tidak ada lagi kematian." },
      { number: 375, pali: "Tatra'yam-adi bhavati...", translation: "Pertama-tama inilah yang harus dikerjakan oleh seorang bhikkhu yang bijaksana, yaitu mengendalikan indria-indria, merasa puas dengan apa yang ada, menjalankan peraturan-peraturan ( = patimokha), serta bergaul dengan teman kehidupan suci ( = sabrahmacari) yang rajin dan bersemangat." },
      { number: 376, pali: "Patisantharavuti assa...", translation: "Hendaklah ia bersikap ramah dan sopan tingkah lakunya. Karena merasa gembira dalam menjalankan hal-hal tersebut, maka ia akan bebas dari penderitaan." },
      { number: 377, pali: "Vassika viya pupphani...", translation: "Seperti tanaman Vassika ( = pohon melati yang merambat) menggugurkan bunga-bunganya sendiri yang layu kering, begitu pula hendaknya engkau, O bhikkhu, membuang nafsu dan dendam." },
      { number: 378, pali: "Santakayo santavaco...", translation: "Seorang bhikkhu yang memiliki perbuatan, ucapan, serta pikiran yang tenang dan terpusat, yang telah dapat menyingkirkan hal-hal duniawi, maka ia adalah orang yang benar-benar damai." },
      { number: 379, pali: "Attana codaya attanam...", translation: "Engkaulah yang harus mengingatkan dan memeriksa dirimu sendiri. O bhikkhu, bila engkau dapat menjaga dirimu sendiri, dan selalu sadar, maka engkau akan hidup dalam kebahagiaan." },
      { number: 380, pali: "Atta hi attano natho...", translation: "Sesungguhnya diri sendiri menjadi tuan bagi diri sendiri. Diri sendiri adalah pelindung bagi diri sendiri. Oleh karena itu, kendalikan dirimu sendiri, seperti pedagang kuda menguasai kuda yang baik." },
      { number: 381, pali: "Pamojjabahulo bhikkhu...", translation: "Dengan penuh kegembiraan dan penuh keyakinan terhadap ajaran Sang Buddha, seorang bhikkhu akan sampai pada keadaan damai (nibbana) disebabkan oleh berakhirnya semua ikatan." },
      { number: 382, pali: "Yo have daharo bhikkhu...", translation: "Walaupun seorang bhikkhu masih berusia muda, namun bila ia tekun menghayati ajaran Sang Buddha, maka ia akan menerangi dunia ini, bagaikan bulan yang terbebas dari awan." }
    ]
  },
  {
    id: 26,
    title: "BRAHMANA VAGGA",
    translation: "Brahmana",
    verses: [
      { number: 383, pali: "Chinda sotam parakkamma...", translation: "O, brahmana, berusahalah dengan tekun memotong arus keinginan dan singkirkanlah nafsu-nafsu indria. Setelah mengetahui penghancuran segala sesuatu yang berkondisi, O, brahmana, engkau akan merealisasi nibbana, ‘Yang Tak Terciptakan’." },
      { number: 384, pali: "Yada dvayesu dhammesu...", translation: "Bila seseorang brahmana telah mencapai akhir daripada dua jalan semadi (pelaksanaan Meditasi Ketenangan dan Pandangan Terang), maka semua belenggu akan terlepas dari dirinya." },
      { number: 385, pali: "Yassa param apāram va...", translation: "Seseorang yang tidak lagi memiliki pantai sini (enam landasan indria dalam) atau pantai sana (enam objek indria luar), ataupun kedua-duanya (pantai sini dan sana), tidak lagi bersedih dan tanpa ikatan, maka ia Kusebut seorang ‘brahmana’." },
      { number: 386, pali: "Jhayim virajam-asinam...", translation: "Seseorang yang tekun bersemadi, bebas dari noda, tenang, telah mengerjakan apa yang harus dikerjakan, bebas dari kekotoran batin dan telah mencapai tujuan akhir (nibbana), maka ia Kusebut seorang ‘brahmana’." },
      { number: 387, pali: "Diva tapati adityo...", translation: "Matahari bersinar di waktu siang. Bulan bercahaya di waktu malam. Ksatria gemerlapan dengan seragam perangnya. Brahmana bersinar terang dalam semadi. Tetapi, Sang Buddha bersinar dengan penuh kemuliaan sepanjang siang dan malam." },
      { number: 388, pali: "Bahitapapo ti brahmano...", translation: "Karena telah membuang kejahatan, maka ia Kusebut seorang ‘brahmana’; karena tingkah lakunya tenang, maka ia Kusebut seorang ‘pertapa’ (samana); dan karena ia telah melenyapkan noda-noda batin, maka ia Kusebut seorang ‘pabbajjita’." },
      { number: 389, pali: "Na brahmanassa pahareyya...", translation: "Janganlah seseorang memukul brahmana, juga janganlah brahmana yang dipukul itu menjadi marah kepadanya. Sungguh memalukan perbuatan orang yang memukul brahmana." },
      { number: 390, pali: "Na brahmanass'etad-akinci seyyo...", translation: "Tak ada yang lebih baik bagi seorang ‘brahmana’ selain menarik pikirannya dari hal-hal yang menyenangkan. Lebih cepat ia dapat menyingkirkan itikad jahatnya, maka lebih cepat pula penderitaannya akan berakhir." },
      { number: 391, pali: "Yassa kayena vacaya...", translation: "Seseorang yang tidak lagi berbuat jahat melalui badan, ucapan, dan pikiran, serta dapat mengendalikan diri dalam tiga saluran perbuatan ini, maka ia Kusebut seorang ‘brahmana’." },
      { number: 392, pali: "Yamhi dhammam vijaneyya...", translation: "Apabila melalui orang lain seseorang dapat mengenal Dhamma sebagaimana yang telah dibabarkan oleh Sang Buddha, maka hendaklah ia menghormati orang tersebut." },
      { number: 393, pali: "Na jatahi na gottena...", translation: "Bukan karena rambut dijalin, keturunan, ataupun kelahiran, seseorang menjadi brahmana. Tetapi orang yang memiliki kejujuran dan kebajikan yang pantas menjadi seorang ‘brahmana’." },
      { number: 394, pali: "Kim te jatahi dummedha...", translation: "Wahai orang bodoh, apa gunanya engkau menjalin rambutmu serta mengenakan pakaian kulit menjangan? Engkau hanya membersihkan bagian luarmu, tetapi hatimu masih penuh dengan kekotoran." },
      { number: 395, pali: "Pamsukuladharam jantum...", translation: "Seseorang yang mengenakan jubah kain bekas (pamsukula), kurus, otot-otot terlihat pada seluruh tubuhnya, bersemadi seorang diri dalam hutan, maka ia Kusebut seorang ‘brahmana’." },
      { number: 396, pali: "Na c'aham brahmanam brumi...", translation: "Aku tidak menyebutnya seorang ‘brahmana’ hanya karena ia berasal dari keluarga brahmana. Apabila dirinya masih penuh dengan noda, maka ia hanyalah seorang brahmana karena keturunan. Tetapi orang yang tanpa noda, maka ia Kusebut seorang ‘brahmana’." },
      { number: 397, pali: "Sabbasamyo janam chetva...", translation: "Ia telah memotiing semua belenggu, tidak lagi gemetar, yang bebas dan telah mematahkan semua ikatan, maka ia Kusebut seorang ‘brahmana’." },
      { number: 398, pali: "Chetva naddhim varattan ca...", translation: "Ia yang telah memotong sabuk kebencian, tali kulit nafsu keinginan dan tali rami pandangan keliru serta semua kekotoran batin laten (anusaya); ia yang telah menyingkirkan kayu penghalang (kebodohan) dan menyadari kebenaran, maka ia Kusebut seorang ‘brahmana’." },
      { number: 399, pali: "Akkosam vadhabandhan ca...", translation: "Seseorang yang tidak marah, yang dapat menahan hinaan, penganiayaan, dan hukuman, yang memiliki senjata kesabaran, maka ia Kusebut seorang ‘brahmana’." },
      { number: 400, pali: "Akkodhanam vatavantam...", translation: "Seseorang yang telah bebas dari kemarahan, taat, bajik, bebas dari nafsu keinginan, dan yang memiliki tubuh ini sebagai tubuh-akhir, maka ia Kusebut seorang ‘brahmana’." },
      { number: 401, pali: "Varipokkharapatte-va...", translation: "Seseorang yang tidak lagi melekat pada kesenangan-kesenangan indria, seperti air di atas daun teratai atau seperti biji lada di ujung jarum, maka ia Kusebut seorang ‘brahmana’." },
      { number: 402, pali: "Yo dukkhassa pajanati...", translation: "Dalam dunia ini, seseorang yang telah menyadari penderitaannya sendiri, yang telah meletakkan beban dan tak terikat, maka ia Kusebut seorang ‘brahmana’." },
      { number: 403, pali: "Gambhirapannam medhavim...", translation: "Seseorang yang pengetahuannya dalam, pandai dan terlatih dalam membedakan jalan yang benar dan salah, yang telah mencapai tujuan tertinggi, maka ia Kusebut seorang ‘brahmana’." },
      { number: 404, pali: "Asamsattham gahatthehi...", translation: "Orang yang menjauhkan diri dari masyarakat umum maupun pertapa, yang mengembara tanpa tempat tinggal tertentu dan sedikit kebutuhannya, maka ia Kusebut seorang ‘brahmana’." },
      { number: 405, pali: "Nidhaya dandam bhutesu...", translation: "Seseorang yang tidak lagi menganiaya makhluk-makhluk lain, baik yang kuat maupun yang lemah, yang tidak membunuh atau menganjurkan orang lain membunuh, maka ia Kusebut seorang ‘brahmana’." },
      { number: 406, pali: "Aviruddham viruddhesu...", translation: "Orang yang tidak membenci di antara mereka yang membenci; damai di antara mereka yang kejam; dan tidak melekat di antara yang melekat, maka ia Kusebut seorang ‘brahmana’." },
      { number: 407, pali: "Yassan rago ca doso ca...", translation: "Seseorang yang nafsunya, kebenciannya, kesombongannya dan kemunafikannya telah gugur, seperti biji lada yang telah jatuh dari ujung jarum, maka ia Kusebut seorang ‘brahmana’." },
      { number: 408, pali: "Akakkasam vinnapanim...", translation: "Seseorang yang mengucapkan kata-kata halus, yang mengandung Ajaran Kebenaran, yang tidak menyinggung siapapun juga, maka ia Kusebut seorang ‘brahmana’." },
      { number: 409, pali: "Yo'dha digham va rassam va...", translation: "Dalam dunia ini, seseorang yang tak mengambil apa yang tidak diberikan, baik yang panjang atau yang pendek, kecil atau besar, baik ataupun buruk, maka ia Kusebut seorang ‘brahmana’." },
      { number: 410, pali: "Asa yassa na vijjanti...", translation: "Seseorang yang tidak mempunyai nafsu keinginan terhadap dunia ini maupun dunia selanjutnya, yang telah bebas dari keinginan, dan tidak lagi melekat, maka ia Kusebut seorang ‘brahmana’." },
      { number: 411, pali: "Yass'alaya na vijjanti...", translation: "Seseorang yang tidak mempunyai nafsu keinginan lagi, yang telah bebas dari keragu-raguan karena memiliki Pengetahuan Sempurna, yang telah menyelami keadaan tanpa kematian (nibbana), maka ia Kusebut seorang ‘brahmana’." },
      { number: 412, pali: "Yo'dha punnan ca papan ca...", translation: "Seseorang yang telah mengatasi kebaikan, kejahatan dan kemelekatan, yang tidak lagi bersedih hati, tanpa noda, dan suci murni, maka ia Kusebut seorang ‘brahmana’." },
      { number: 413, pali: "Candam-va vimalam suddham...", translation: "Seseorang yang tanpa noda, bersih, tenang, dan jernih batinnya seperti bulan purnama, maka ia Kusebut seorang ‘brahmana’." },
      { number: 414, pali: "Yo imam palipatham duggam...", translation: "Orang yang telah menyeberangi lautan kehidupan (samsara) yang kotor, berbahaya dan bersifat maya; yang telah menyeberang dan mencapai ‘Pantai Seberang’ (nibbana); yang selalu bersemadi, tenang, dan bebas dari keragu-raguan; maka ia Kusebut seorang ‘brahmana’." },
      { number: 415, pali: "Yo'dha kame pahatvana...", translation: "Seseorang yang dengan membuang nafsu keinginan kemudian meninggalkan kehidupan rumah-tangga dan menempuh kehidupan tanpa rumah, yang telah menghancurkan nafsu indria akan ujud yang baru, maka ia Kusebut seorang ‘brahmana’." },
      { number: 416, pali: "Yo'dha tanham pahatvana...", translation: "Seseorang yang dengan membuang nafsu keinginan kemudian meninggalkan kehidupan rumah-tangga, dan menempuh kehidupan tanpa rumah, yang telah menghancurkan kemelekatan dan kerinduan, maka ia Kusebut seorang ‘brahmana’." },
      { number: 417, pali: "Hitva manusakam yogam...", translation: "Seseorang yang telah menyingkirkan ikatan-ikatan duniawi dan juga telah mengatasi ikatan-ikatan surgawi, yang benar-benar telah bebas dari semua ikatan, maka ia Kusebut seorang ‘brahmana’." },
      { number: 418, pali: "Hitva ratin ca aratin ca...", translation: "Seseorang yang telah mengatasi rasa senang dan tidak senang dengan tidak menghiraukannya lagi, yang telah menghancurkan dasar-dasar bagi perwujudan, maka ia Kusebut seorang ‘brahmana’." },
      { number: 419, pali: "Cutim yo vedi sattanam...", translation: "Seseorang yang telah memiliki pengetahuan sempurna tentang timbul dan lenyapnya makhluk-makhluk, yang telah bebas dari ikatan, telah pergi dengan baik (Sugata) dan telah mencapai ‘Penerangan Sempurna’, maka ia Kusebut seorang ‘brahmana’." },
      { number: 420, pali: "Yassa gatim na jananti...", translation: "Orang yang jejaknya tak dapat dilacak, baik oleh para dewa, gandarwa, maupun manusia, yang telah menghancurkan semua kekotoran batin dan telah mencapai kesucian (arahat), maka ia Kusebut seorang ‘brahmana’." },
      { number: 421, pali: "Yassa pure ca paccha ca...", translation: "Orang yang tidak lagi terikat pada apa yang telah lampau, apa yang sekarang maupun yang akan datang, yang tidak memegang ataupun melekat pada apapun juga, maka ia Kusebut seorang ‘brahmana’." },
      { number: 422, pali: "Usabham pavaram viram...", translation: "Ia yang mulia, agung, pahlawan, pertapa agung (mahesi), penakluk, orang tanpa nafsu, murni, telah mencapai penerangan, maka ia Kusebut seorang ‘brahmana’." },
      { number: 423, pali: "Pubbenivasam yo vedi...", translation: "Seseorang yang mengetahui semua kehidupannya yang lampau, yang dapat melihat keadaan surga dan neraka, yang telah mencapai akhir kelahiran, telah mencapai kesempurnaan pandangan terang, suci, murni, dan sempurna kebijaksanannya, maka ia Kusebut seorang ‘brahmana’." }
    ]
  }
];
